// src/pages/FirewallGroupsPage.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";

function FirewallGroupsPage() {
  const [groups, setGroups] = useState([]);
  const [groupName, setGroupName] = useState("");
  const [groupDesc, setGroupDesc] = useState("");

  const [showRules, setShowRules] = useState(false);
  const [selectedGroupRules, setSelectedGroupRules] = useState([]);
  const [selectedGroupName, setSelectedGroupName] = useState("");

  useEffect(() => {
    fetchGroups();
  }, []);

  const fetchGroups = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://127.0.0.1:8000/firewall/groups", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setGroups(res.data);
    } catch (err) {
      alert("Grup listesi alınamadı: " + err.message);
    }
  };

  const handleCreateGroup = async (e) => {
    e.preventDefault();
    if (!groupName) {
      alert("Grup adı giriniz!");
      return;
    }
    try {
      const token = localStorage.getItem("token");
      const body = {
        group_name: groupName,
        description: groupDesc
      };
      const res = await axios.post("http://127.0.0.1:8000/firewall/groups", body, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert(res.data.message);
      setGroupName("");
      setGroupDesc("");
      fetchGroups();
    } catch (err) {
      alert("Grup ekleme hatası: " + (err.response?.data?.detail || err.message));
    }
  };

  const handleDeleteGroup = async (group) => {
    if (!window.confirm(`Grup '${group.group_name}' silinsin mi?`)) return;
    try {
      const token = localStorage.getItem("token");
      const url = `http://127.0.0.1:8000/firewall/groups/${group._id}`;
      const res = await axios.delete(url, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert(res.data.message);
      fetchGroups();
    } catch (err) {
      alert("Grup silme hatası: " + (err.response?.data?.detail || err.message));
    }
  };

  const handleToggleGroup = async (group, enable) => {
    if (!window.confirm(
      `Grup '${group.group_name}' kuralları ${enable ? 'etkin' : 'pasif'} yapılsın mı?`
    )) {
      return;
    }
    try {
      const token = localStorage.getItem("token");
      const url = `http://127.0.0.1:8000/firewall/groups/${group._id}/enable?enable=${enable}`;
      const res = await axios.patch(url, null, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert(res.data.message);
    } catch (err) {
      alert("Toplu enable/disable hatası: " + (err.response?.data?.detail || err.message));
    }
  };

  const handleShowRules = async (group) => {
    try {
      const token = localStorage.getItem("token");
      const url = `http://127.0.0.1:8000/firewall/groups/${group._id}/rules`;
      const res = await axios.get(url, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setSelectedGroupRules(res.data);
      setSelectedGroupName(group.group_name);
      setShowRules(true);
    } catch (err) {
      alert("Grup kuralları alınamadı: " + (err.response?.data?.detail || err.message));
    }
  };

  return (
    <div>
      <h2 className="dashboard-title">Kural Grupları</h2>

      <div className="card mb-4">
        <div className="card-header">Mevcut Gruplar</div>
        <div className="card-body p-0">
          <table className="table mb-0">
            <thead>
              <tr>
                <th>Grup Adı</th>
                <th>Açıklama</th>
                <th>İşlem</th>
              </tr>
            </thead>
            <tbody>
              {groups.map((g) => (
                <tr key={g._id}>
                  <td>{g.group_name}</td>
                  <td>{g.description}</td>
                  <td>
                    <button
                      className="btn btn-sm btn-info me-2"
                      onClick={() => handleShowRules(g)}
                    >
                      Kuralları Gör
                    </button>
                    <button
                      className="btn btn-sm btn-success me-2"
                      onClick={() => handleToggleGroup(g, true)}
                    >
                      Etkinle
                    </button>
                    <button
                      className="btn btn-sm btn-warning me-2"
                      onClick={() => handleToggleGroup(g, false)}
                    >
                      Pasifleştir
                    </button>
                    <button
                      className="btn btn-sm btn-danger"
                      onClick={() => handleDeleteGroup(g)}
                    >
                      Sil
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Grup ekleme */}
      <div className="card mb-3">
        <div className="card-header">Yeni Grup Ekle</div>
        <div className="card-body">
          <form onSubmit={handleCreateGroup}>
            <div className="mb-3">
              <label>Grup Adı</label>
              <input
                className="form-control"
                value={groupName}
                onChange={(e) => setGroupName(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <label>Açıklama</label>
              <input
                className="form-control"
                value={groupDesc}
                onChange={(e) => setGroupDesc(e.target.value)}
              />
            </div>
            <button type="submit" className="btn btn-primary">Ekle</button>
          </form>
        </div>
      </div>

      {/* Grup Detay Modal benzeri */}
      {showRules && (
        <div className="card">
          <div className="card-header">
            "{selectedGroupName}" Grubundaki Kurallar
            <button
              className="btn btn-sm btn-secondary float-end"
              onClick={() => setShowRules(false)}
            >
              Kapat
            </button>
          </div>
          <div className="card-body p-0">
            {selectedGroupRules.length === 0 ? (
              <p className="p-3">Bu grupta kural yok.</p>
            ) : (
              <table className="table mb-0">
                <thead>
                  <tr>
                    <th>Kural Adı</th>
                    <th>Action</th>
                    <th>Port</th>
                    <th>Enabled?</th>
                  </tr>
                </thead>
                <tbody>
                  {selectedGroupRules.map((r) => (
                    <tr key={r._id}>
                      <td>{r.rule_name}</td>
                      <td>{r.action}</td>
                      <td>{r.port || "-"}</td>
                      <td>{r.enabled ? "Evet" : "Hayır"}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default FirewallGroupsPage;
