import React from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";

import Layout from "./Layout";
import LoginPage from "./pages/LoginPage";
import DashboardPage from "./pages/DashboardPage";
import LogsPage from "./pages/LogsPage";
import UpdatesPage from "./pages/UpdatesPage";
import ReportsPage from "./pages/ReportsPage";
import SettingsPage from "./pages/SettingsPage";
import FirewallRulesPage from "./pages/FirewallRulesPage";
import NetworkInterfacesPage from "./pages/NetworkInterfacesPage";
import NatSettingsPage from "./pages/NatSettingsPage";
import StaticRoutesPage from "./pages/StaticRoutesPage";
import DomainBlockPage from "./pages/DomainBlockPage"; // DNS engelleme sayfası

function App() {
  return (
    <BrowserRouter>
      <Routes>
        {/* Kök path => login'e yönlendir */}
        <Route path="/" element={<Navigate to="/login" replace />} />

        {/* Login */}
        <Route path="/login" element={<LoginPage />} />

        {/* Dashboard (Layout içerikli) */}
        <Route
          path="/dashboard"
          element={
            <Layout>
              <DashboardPage />
            </Layout>
          }
        />
        <Route
          path="/logs"
          element={
            <Layout>
              <LogsPage />
            </Layout>
          }
        />
        <Route
          path="/updates"
          element={
            <Layout>
              <UpdatesPage />
            </Layout>
          }
        />
        <Route
          path="/reports"
          element={
            <Layout>
              <ReportsPage />
            </Layout>
          }
        />
        <Route
          path="/settings"
          element={
            <Layout>
              <SettingsPage />
            </Layout>
          }
        />
        <Route
          path="/rules"
          element={
            <Layout>
              <FirewallRulesPage />
            </Layout>
          }
        />
        <Route
          path="/network"
          element={
            <Layout>
              <NetworkInterfacesPage />
            </Layout>
          }
        />
        <Route
          path="/nat"
          element={
            <Layout>
              <NatSettingsPage />
            </Layout>
          }
        />
        <Route
          path="/routes"
          element={
            <Layout>
              <StaticRoutesPage />
            </Layout>
          }
        />

        {/* DNS Engelleme */}
        <Route
          path="/dns"
          element={
            <Layout>
              <DomainBlockPage />
            </Layout>
          }
        />

        {/* 404 fallback */}
        <Route path="*" element={<Navigate to="/login" replace />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
