// src/pages/NetworkInterfacesPage.jsx

import React, { useEffect, useState } from 'react';
import axios from 'axios';

function NetworkInterfacesPage() {
  // Liste (DB + netsh show interface)
  const [interfaces, setInterfaces] = useState([]);

  // "Yeni Ekle" form
  const [intfName, setIntfName] = useState("Ethernet");
  const [ipMode, setIpMode] = useState("static"); // "static" or "dhcp"
  const [ipAddress, setIpAddress] = useState("");
  const [subnetMask, setSubnetMask] = useState("");
  const [gateway, setGateway] = useState("");
  const [dnsPrimary, setDnsPrimary] = useState("");
  const [dnsSecondary, setDnsSecondary] = useState("");
  const [adminEnabled, setAdminEnabled] = useState(true);
  const [mtu, setMtu] = useState(1500);
  const [vlanId, setVlanId] = useState("");

  // "Edit mode"
  const [isEditMode, setIsEditMode] = useState(false);

  // Edit states
  const [editInterfaceName, setEditInterfaceName] = useState("");
  const [editIpMode, setEditIpMode] = useState("static");
  const [editIp, setEditIp] = useState("");
  const [editMask, setEditMask] = useState("");
  const [editGateway, setEditGateway] = useState("");
  const [editDnsPrimary, setEditDnsPrimary] = useState("");
  const [editDnsSecondary, setEditDnsSecondary] = useState("");
  const [editAdminEnabled, setEditAdminEnabled] = useState(true);
  const [editMtu, setEditMtu] = useState(1500);
  const [editVlanId, setEditVlanId] = useState("");

  useEffect(() => {
    fetchInterfaces();
  }, []);

  const fetchInterfaces = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get("http://127.0.0.1:8000/network/interfaces", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setInterfaces(res.data);
    } catch (err) {
      console.error(err);
      alert("Arayüzleri çekerken hata: " + err.message);
    }
  };

  // Yeni ekleme (POST)
  const handleSave = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const body = {
        interface_name: intfName,
        ip_mode: ipMode,
        ip_address: ipAddress || null,
        subnet_mask: subnetMask || null,
        gateway: gateway || null,
        dns_primary: dnsPrimary || null,
        dns_secondary: dnsSecondary || null,
        admin_enabled: adminEnabled,
        mtu: mtu ? Number(mtu) : null,
        vlan_id: vlanId ? Number(vlanId) : null,
      };
      const res = await axios.post(
        "http://127.0.0.1:8000/network/interfaces",
        body,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert(res.data.message || "Arayüz eklendi");
      // sıfırla
      setIpMode("static");
      setIpAddress("");
      setSubnetMask("");
      setGateway("");
      setDnsPrimary("");
      setDnsSecondary("");
      setAdminEnabled(true);
      setMtu(1500);
      setVlanId("");
      fetchInterfaces();
    } catch (err) {
      console.error(err);
      alert("Kayıt hatası: " + (err.response?.data?.detail || err.message));
    }
  };

  // Edit
  const handleEditClick = (inf) => {
    setIsEditMode(true);
    setEditInterfaceName(inf.interface_name);
    setEditIpMode(inf.ip_mode || "static");
    setEditIp(inf.ip_address || "");
    setEditMask(inf.subnet_mask || "");
    setEditGateway(inf.gateway || "");
    setEditDnsPrimary(inf.dns_primary || "");
    setEditDnsSecondary(inf.dns_secondary || "");
    setEditAdminEnabled(inf.admin_enabled !== false);
    setEditMtu(inf.mtu || 1500);
    setEditVlanId(inf.vlan_id || "");
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const body = {
        interface_name: editInterfaceName,
        ip_mode: editIpMode,
        ip_address: editIp || null,
        subnet_mask: editMask || null,
        gateway: editGateway || null,
        dns_primary: editDnsPrimary || null,
        dns_secondary: editDnsSecondary || null,
        admin_enabled: editAdminEnabled,
        mtu: editMtu ? Number(editMtu) : null,
        vlan_id: editVlanId ? Number(editVlanId) : null
      };
      const url = `http://127.0.0.1:8000/network/interfaces/${editInterfaceName}`;
      const res = await axios.put(url, body, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert(res.data.message || "Güncellendi");
      setIsEditMode(false);
      fetchInterfaces();
    } catch (err) {
      console.error(err);
      alert("Güncelleme hatası: " + (err.response?.data?.detail || err.message));
    }
  };

  const handleCancelEdit = () => {
    setIsEditMode(false);
  };

  // Yardımcı fonksiyon: DB'deki ISO datetime'ı "Istanbul local" formatına çevirmek
  const formatDateIstanbul = (isoString) => {
    if (!isoString) return "-";
    // new Date(...) => Date objesi
    const dt = new Date(isoString);
    // "tr-TR" + timeZone => "25.05.2025 22:40:05" gibi (saat/doğrulaması)
    return dt.toLocaleString("tr-TR", { timeZone: "Europe/Istanbul" });
  };

  return (
    <div>
      <h2 className="dashboard-title">Network Interface Ayarları (Gelişmiş)</h2>

      {/* Mevcut arayüz listesi */}
      <div className="card mb-3">
        <div className="card-header">Mevcut Interface Ayarları</div>
        <div className="card-body p-0">
          <table className="table mb-0">
            <thead>
              <tr>
                <th>Arayüz Adı</th>
                <th>Mode</th>
                <th>IP/Subnet</th>
                <th>Gateway</th>
                <th>DNS Primary</th>
                <th>DNS Secondary</th>
                <th>Admin</th>
                <th>Link State</th>
                <th>MTU</th>
                <th>VLAN</th>
                <th>Güncellendi</th>
                <th>İşlem</th>
              </tr>
            </thead>
            <tbody>
              {interfaces.map(inf => (
                <tr key={inf._id}>
                  <td>{inf.interface_name}</td>
                  <td>{inf.ip_mode}</td>
                  <td>
                    {inf.ip_address || "-"} / {inf.subnet_mask || "-"}
                  </td>
                  <td>{inf.gateway || "-"}</td>
                  <td>{inf.dns_primary || "-"}</td>
                  <td>{inf.dns_secondary || "-"}</td>
                  <td>{inf.admin_enabled ? "Enabled" : "Disabled"}</td>
                  <td>{inf.link_state || "?"}</td>
                  <td>{inf.mtu || "-"}</td>
                  <td>{inf.vlan_id || "-"}</td>
                  <td>
                    {/* Burada iso datetime'ı formatlı göstermek */}
                    {formatDateIstanbul(inf.updated_at)}
                  </td>
                  <td>
                    <button
                      className="btn btn-sm btn-primary"
                      onClick={() => handleEditClick(inf)}
                    >
                      Düzenle
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {isEditMode ? (
        <div className="card">
          <div className="card-header">Arayüz Güncelle</div>
          <div className="card-body">
            <form onSubmit={handleUpdate}>
              <div className="mb-3">
                <label>Interface Adı</label>
                <input
                  className="form-control"
                  value={editInterfaceName}
                  disabled
                  onChange={()=>{}}
                />
              </div>

              <div className="mb-3">
                <label>Mode (DHCP/Static)</label>
                <select
                  className="form-select"
                  value={editIpMode}
                  onChange={(e) => setEditIpMode(e.target.value)}
                >
                  <option value="static">Statik</option>
                  <option value="dhcp">DHCP</option>
                </select>
              </div>

              {editIpMode === "static" && (
                <>
                  <div className="mb-3">
                    <label>IP Address</label>
                    <input
                      className="form-control"
                      value={editIp}
                      onChange={(e) => setEditIp(e.target.value)}
                    />
                  </div>
                  <div className="mb-3">
                    <label>Subnet Mask</label>
                    <input
                      className="form-control"
                      value={editMask}
                      onChange={(e) => setEditMask(e.target.value)}
                    />
                  </div>
                  <div className="mb-3">
                    <label>Gateway (opsiyonel)</label>
                    <input
                      className="form-control"
                      value={editGateway}
                      onChange={(e) => setEditGateway(e.target.value)}
                    />
                  </div>
                  <div className="mb-3">
                    <label>DNS Primary (opsiyonel)</label>
                    <input
                      className="form-control"
                      value={editDnsPrimary}
                      onChange={(e) => setEditDnsPrimary(e.target.value)}
                    />
                  </div>
                  <div className="mb-3">
                    <label>DNS Secondary (opsiyonel)</label>
                    <input
                      className="form-control"
                      value={editDnsSecondary}
                      onChange={(e) => setEditDnsSecondary(e.target.value)}
                    />
                  </div>
                </>
              )}

              <div className="mb-3 form-check">
                <input
                  className="form-check-input"
                  type="checkbox"
                  checked={editAdminEnabled}
                  onChange={(e) => setEditAdminEnabled(e.target.checked)}
                  id="chkAdmin"
                />
                <label className="form-check-label" htmlFor="chkAdmin">
                  Arayüz Etkin (Up/Down)
                </label>
              </div>

              <div className="mb-3">
                <label>MTU</label>
                <input
                  className="form-control"
                  type="number"
                  value={editMtu}
                  onChange={(e) => setEditMtu(e.target.value)}
                />
              </div>

              <div className="mb-3">
                <label>VLAN ID (opsiyonel)</label>
                <input
                  className="form-control"
                  type="number"
                  value={editVlanId}
                  onChange={(e) => setEditVlanId(e.target.value)}
                />
              </div>

              <button className="btn btn-success" type="submit">Kaydet</button>
              <button className="btn btn-secondary ms-2" type="button" onClick={handleCancelEdit}>
                Vazgeç
              </button>
            </form>
          </div>
        </div>
      ) : (
        // Yeni ekleme formu
        <div className="card">
          <div className="card-header">Yeni/Değiştir IP Ayarı</div>
          <div className="card-body">
            <form onSubmit={handleSave}>
              <div className="mb-3">
                <label className="form-label">Interface Adı</label>
                <input
                  type="text"
                  className="form-control"
                  value={intfName}
                  onChange={(e) => setIntfName(e.target.value)}
                />
              </div>

              <div className="mb-3">
                <label>Mode (DHCP/Static)</label>
                <select
                  className="form-select"
                  value={ipMode}
                  onChange={(e) => setIpMode(e.target.value)}
                >
                  <option value="static">Statik</option>
                  <option value="dhcp">DHCP</option>
                </select>
              </div>

              {ipMode === "static" && (
                <>
                  <div className="mb-3">
                    <label>IP Address</label>
                    <input
                      type="text"
                      className="form-control"
                      value={ipAddress}
                      onChange={(e) => setIpAddress(e.target.value)}
                      placeholder="192.168.1.50"
                    />
                  </div>
                  <div className="mb-3">
                    <label>Subnet Mask</label>
                    <input
                      type="text"
                      className="form-control"
                      value={subnetMask}
                      onChange={(e) => setSubnetMask(e.target.value)}
                      placeholder="255.255.255.0"
                    />
                  </div>
                  <div className="mb-3">
                    <label>Gateway (opsiyonel)</label>
                    <input
                      type="text"
                      className="form-control"
                      value={gateway}
                      onChange={(e) => setGateway(e.target.value)}
                      placeholder="192.168.1.1"
                    />
                  </div>
                  <div className="mb-3">
                    <label>DNS Primary (opsiyonel)</label>
                    <input
                      type="text"
                      className="form-control"
                      value={dnsPrimary}
                      onChange={(e) => setDnsPrimary(e.target.value)}
                      placeholder="8.8.8.8"
                    />
                  </div>
                  <div className="mb-3">
                    <label>DNS Secondary (opsiyonel)</label>
                    <input
                      type="text"
                      className="form-control"
                      value={dnsSecondary}
                      onChange={(e) => setDnsSecondary(e.target.value)}
                      placeholder="8.8.4.4"
                    />
                  </div>
                </>
              )}

              <div className="mb-3 form-check">
                <input
                  className="form-check-input"
                  type="checkbox"
                  checked={adminEnabled}
                  onChange={(e) => setAdminEnabled(e.target.checked)}
                  id="chkAdminNew"
                />
                <label className="form-check-label" htmlFor="chkAdminNew">
                  Arayüz Etkin (Up/Down)
                </label>
              </div>

              <div className="mb-3">
                <label>MTU</label>
                <input
                  type="number"
                  className="form-control"
                  value={mtu}
                  onChange={(e) => setMtu(e.target.value)}
                />
              </div>

              <div className="mb-3">
                <label>VLAN ID (opsiyonel)</label>
                <input
                  type="number"
                  className="form-control"
                  value={vlanId}
                  onChange={(e) => setVlanId(e.target.value)}
                />
              </div>

              <button className="btn btn-primary" type="submit">
                Kaydet
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default NetworkInterfacesPage;
