// src/App.js
import React from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';

import Layout from './Layout';
import LoginPage from './pages/LoginPage';
import DashboardPage from './pages/DashboardPage';
import LogsPage from './pages/LogsPage';
import FirewallRulesPage from './pages/FirewallRulesPage';
import UpdatesPage from './pages/UpdatesPage';
import ReportsPage from './pages/ReportsPage';
import SettingsPage from './pages/SettingsPage';

// Network / NAT / Routes
import NetworkInterfacesPage from './pages/NetworkInterfacesPage';
import NatSettingsPage from './pages/NatSettingsPage';
import StaticRoutesPage from './pages/StaticRoutesPage';  // Statik rota yönetimi

function App() {
  return (
    <BrowserRouter>
      <Routes>
        {/* Login sayfası (Layout olmadan) */}
        <Route path="/login" element={<LoginPage />} />

        {/* Layout ile korunan sayfalar */}
        <Route
          path="/"
          element={
            <Layout>
              <DashboardPage />
            </Layout>
          }
        />
        <Route
          path="/logs"
          element={
            <Layout>
              <LogsPage />
            </Layout>
          }
        />
        <Route
          path="/updates"
          element={
            <Layout>
              <UpdatesPage />
            </Layout>
          }
        />
        <Route
          path="/rules"
          element={
            <Layout>
              <FirewallRulesPage />
            </Layout>
          }
        />
        <Route
          path="/reports"
          element={
            <Layout>
              <ReportsPage />
            </Layout>
          }
        />
        <Route
          path="/settings"
          element={
            <Layout>
              <SettingsPage />
            </Layout>
          }
        />

        {/* Network alt sayfalar */}
        <Route
          path="/network"
          element={
            <Layout>
              <NetworkInterfacesPage />
            </Layout>
          }
        />
        <Route
          path="/nat"
          element={
            <Layout>
              <NatSettingsPage />
            </Layout>
          }
        />
        <Route
          path="/routes"
          element={
            <Layout>
              <StaticRoutesPage />
            </Layout>
          }
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
