// src/pages/LoginPage.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function LoginPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      // Burada backend'e /api/auth/login'e post isteği atılıyor örneğin
      const resp = await axios.post("/api/auth/login", { username, password });
      // Dönen data içinde token olduğunu varsayıyoruz:
      const data = resp.data;
      localStorage.setItem("token", data.access_token);
      alert("Giriş başarılı!");
      navigate("/"); // anasayfa rotasına yönlendir
    } catch (error) {
      alert("Giriş hatası: " + (error.response?.data?.detail || error.message));
    }
  };

  return (
    <div className="d-flex justify-content-center align-items-center" style={{ minHeight: "100vh" }}>
      <div className="card shadow-sm" style={{ width: "400px" }}>
        <div className="card-body">
          <h3 className="card-title mb-4 text-center">Giriş Yap</h3>

          <div className="mb-3">
            <label className="form-label">Kullanıcı Adı</label>
            <input
              type="text"
              className="form-control"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Parola</label>
            <input
              type="password"
              className="form-control"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>

          <button className="btn btn-primary w-100" onClick={handleLogin}>
            Giriş
          </button>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
