// YENİ: Birden fazla IP, Zamanlama, Priority, vs.

function FirewallRulesPage() {
  const [rules, setRules] = useState([]);

  // Ek alanlar:
  const [sourceIps, setSourceIps] = useState("");
  const [enabled, setEnabled] = useState(true);
  const [priority, setPriority] = useState(100);
  const [scheduleStart, setScheduleStart] = useState("");
  const [scheduleEnd, setScheduleEnd] = useState("");
  const [daysOfWeek, setDaysOfWeek] = useState([]);

  // ...
  const handleEditClick = (r) => {
    // r.source_ips => virgül birleştir
    setSourceIps(r.source_ips ? r.source_ips.join(",") : "");
    setEnabled(r.enabled !== false);
    setPriority(r.priority || 100);
    setScheduleStart(r.schedule_start || "");
    setScheduleEnd(r.schedule_end || "");
    setDaysOfWeek(r.days_of_week || []);
    // ...
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Virgülle girilen IP’leri array'e dönüştür:
    const ipsArray = sourceIps
      .split(",")
      .map((ip) => ip.trim())
      .filter((ip) => ip !== "");

    const body = {
      rule_name,
      source_ips: ipsArray.length ? ipsArray : null,
      port: port ? Number(port) : null,
      protocol,
      action,
      direction,
      profile,
      description,
      enabled,
      priority: Number(priority),
      schedule_start: scheduleStart || null,
      schedule_end: scheduleEnd || null,
      days_of_week: daysOfWeek.map(Number),
    };

    // POST veya PUT
  };

  return (
    <div>
      {/* Aynı tablo, eklenmiş sütunlar: "Enabled", "Priority", "Schedule" */}
      {/* Formda da "Birden fazla IP", "Enabled" checkbox, "Priority" input vb. */}
    </div>
  );
}
