// src/App.js
import React from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import Layout from "./Layout";
import LoginPage from "./pages/LoginPage";
import DashboardPage from "./pages/DashboardPage";

import LogsPage from "./pages/LogsPage";
import UpdatesPage from "./pages/UpdatesPage";
import ReportsPage from "./pages/ReportsPage";
import SettingsPage from "./pages/SettingsPage";

// ÖNEMLİ: Diğer sayfalar
import FirewallRulesPage from "./pages/FirewallRulesPage";
import NetworkInterfacesPage from "./pages/NetworkInterfacesPage";
import NatSettingsPage from "./pages/NatSettingsPage";
import StaticRoutesPage from "./pages/StaticRoutesPage";

function App() {
  return (
    <BrowserRouter>
      <Routes>

        {/* Giriş - Layoutsuz */}
        <Route path="/login" element={<LoginPage />} />

        {/* Layout'lu korunan rotalar */}
        <Route
          path="/"
          element={
            <Layout>
              <DashboardPage />
            </Layout>
          }
        />

        <Route
          path="/logs"
          element={
            <Layout>
              <LogsPage />
            </Layout>
          }
        />
        <Route
          path="/updates"
          element={
            <Layout>
              <UpdatesPage />
            </Layout>
          }
        />
        <Route
          path="/reports"
          element={
            <Layout>
              <ReportsPage />
            </Layout>
          }
        />
        <Route
          path="/settings"
          element={
            <Layout>
              <SettingsPage />
            </Layout>
          }
        />

        {/* Firewall Kuralları */}
        <Route
          path="/rules"
          element={
            <Layout>
              <FirewallRulesPage />
            </Layout>
          }
        />

        {/* Network alt menü */}
        <Route
          path="/network"
          element={
            <Layout>
              <NetworkInterfacesPage />
            </Layout>
          }
        />
        <Route
          path="/nat"
          element={
            <Layout>
              <NatSettingsPage />
            </Layout>
          }
        />
        <Route
          path="/routes"
          element={
            <Layout>
              <StaticRoutesPage />
            </Layout>
          }
        />

      </Routes>
    </BrowserRouter>
  );
}

export default App;
