import React, { useState } from "react";

function LoginPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();

    // Basit kontrol: kullanıcı = "admin" ve parola = "admin123"
    if (username === "admin" && password === "admin123") {
      // Sahte bir token
      localStorage.setItem("token", "fake-jwt-token");
      alert("Giriş başarılı! admin / admin123");

      // Örnek yönlendirme: anasayfa
      window.location.href = "/";
    } else {
      alert("Giriş hatası: Geçersiz kullanıcı adı veya şifre!");
    }
  };

  return (
    <div
      className="d-flex justify-content-center align-items-center vh-100"
      style={{ backgroundColor: "#f3f4f6" }}
    >
      <div className="card shadow" style={{ width: "420px" }}>
        <div className="card-header text-center bg-primary text-white">
          <h4 className="mb-0">NetGate Firewall Login</h4>
        </div>

        <div className="card-body">
          <form onSubmit={handleLogin}>
            {/* Kullanıcı Adı */}
            <div className="mb-3">
              <label className="form-label">Kullanıcı Adı</label>
              <input
                type="text"
                className="form-control"
                // Hiçbir placeholder veya defaultValue ayarı yok.
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>

            {/* Parola */}
            <div className="mb-3">
              <label className="form-label">Parola</label>
              <input
                type="password"
                className="form-control"
                // Hiçbir placeholder veya defaultValue ayarı yok.
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>

            {/* Giriş Butonu */}
            <button type="submit" className="btn btn-primary w-100">
              Giriş
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
