// src/pages/LoginPage.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function LoginPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      // örnek: gerçekte /api/auth/login
      const response = await axios.post("/api/auth/login", { username, password });
      const data = response.data;
      localStorage.setItem("token", data.access_token);
      alert("Giriş başarılı!");
      navigate("/"); // Giriş sonrası anasayfaya yönlendir
    } catch (error) {
      alert("Giriş hatası: " + (error.response?.data?.detail || error.message));
    }
  };

  return (
    <div className="d-flex align-items-center justify-content-center" style={{ minHeight: '100vh', backgroundColor: '#EEF0F3' }}>
      <div className="login-card">
        <h3 className="text-center mb-4" style={{ color: '#2B6CB0' }}>NetGate Firewall</h3>
        <div className="mb-3">
          <label className="form-label">Kullanıcı Adı</label>
          <input
            type="text"
            className="form-control"
            value={username}
            onChange={e => setUsername(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Parola</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={e => setPassword(e.target.value)}
          />
        </div>
        <button className="btn btn-primary w-100 mt-2" onClick={handleLogin}>
          Giriş Yap
        </button>
      </div>
    </div>
  );
}

export default LoginPage;
