// File: src/pages/DNSManagementPage.jsx

import React, { useState, useEffect } from "react";
import axios from "axios";

function DNSManagementPage() {
  // 1) Basit Domain Engelleme
  const [domains, setDomains] = useState([]);
  const [newDomain, setNewDomain] = useState("");
  const [useWildcard, setUseWildcard] = useState(true);
  const [note, setNote] = useState("");

  // 2) Adblock Liste Ekleme
  const [adblockUrl, setAdblockUrl] = useState("");
  const [adblockStatus, setAdblockStatus] = useState("");

  // YENİ: Çoklu seçim için state
  const [selectedDomains, setSelectedDomains] = useState([]); // Silmek üzere seçilen domain listesi

  useEffect(() => {
    fetchDomains();
  }, []);

  const fetchDomains = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://127.0.0.1:8000/dns/domains", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setDomains(res.data);
      setSelectedDomains([]); // Her yüklemede seçim sıfırlansın
    } catch (err) {
      alert("Domain listesi çekilemedi: " + err.message);
    }
  };

  const handleAddDomain = async (e) => {
    e.preventDefault();
    if (!newDomain) {
      alert("Lütfen engellenecek domaini girin!");
      return;
    }
    try {
      const token = localStorage.getItem("token");
      const body = {
        domain: newDomain.trim().toLowerCase(),
        use_wildcard: useWildcard,
        note,
      };
      const res = await axios.post("http://127.0.0.1:8000/dns/domains", body, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert(res.data.message || "Domain engellendi.");
      setNewDomain("");
      setUseWildcard(true);
      setNote("");
      fetchDomains();
    } catch (err) {
      alert(
        "Domain ekleme hatası: " + (err.response?.data?.detail || err.message)
      );
    }
  };

  // Tek tek silme (eski yöntem, hala kullanabiliriz)
  const handleDeleteDomain = async (dom) => {
    if (!window.confirm(`"${dom.domain}" listesinden çıkarılsın mı?`)) return;
    try {
      const token = localStorage.getItem("token");
      const url = `http://127.0.0.1:8000/dns/domains/${dom.domain}`;
      const res = await axios.delete(url, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert(res.data.message || "Silindi");
      fetchDomains();
    } catch (err) {
      alert("Silme hatası: " + (err.response?.data?.detail || err.message));
    }
  };

  // YENİ: Çoklu seçim/checkbox handle
  const handleCheckboxChange = (dom) => {
    // dom = bir domain objesi
    // eğer zaten selectedDomains içindeyse çıkart, yoksa ekle
    if (selectedDomains.includes(dom.domain)) {
      // zaten seçili -> çıkart
      setSelectedDomains(selectedDomains.filter((d) => d !== dom.domain));
    } else {
      // seçili değil -> ekle
      setSelectedDomains([...selectedDomains, dom.domain]);
    }
  };

  // YENİ: Tüm satırları seç/deselect
  const handleSelectAll = (e) => {
    if (e.target.checked) {
      // Tüm domainleri ekle
      const allDomainNames = domains.map((d) => d.domain);
      setSelectedDomains(allDomainNames);
    } else {
      // Hepsini temizle
      setSelectedDomains([]);
    }
  };

  // YENİ: Seçilenleri toplu sil
  const handleDeleteSelected = async () => {
    if (selectedDomains.length === 0) {
      alert("Hiçbir domain seçilmedi!");
      return;
    }
    if (
      !window.confirm(
        `Seçili ${selectedDomains.length} domaini silmek istediğinize emin misiniz?`
      )
    ) {
      return;
    }

    try {
      const token = localStorage.getItem("token");
      // Basit yöntem: her domain için teker teker delete isteği at.
      for (const domainName of selectedDomains) {
        const url = `http://127.0.0.1:8000/dns/domains/${domainName}`;
        await axios.delete(url, {
          headers: { Authorization: `Bearer ${token}` },
        });
      }
      alert("Seçilen domainler silindi.");
      fetchDomains();
    } catch (err) {
      alert("Toplu silme hatası: " + (err.response?.data?.detail || err.message));
    }
  };

  // AdBlock listeyi indirme
  const handleAdblockImport = async () => {
    if (!adblockUrl) {
      alert("Lütfen AdBlock liste URL'si girin!");
      return;
    }
    try {
      const token = localStorage.getItem("token");
      setAdblockStatus("İndiriliyor...");
      const body = { url: adblockUrl };
      const res = await axios.post("http://127.0.0.1:8000/dns/adblocklist", body, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAdblockStatus(res.data.message || "Liste eklendi");
      fetchDomains();
    } catch (err) {
      setAdblockStatus("Hata: " + (err.response?.data?.detail || err.message));
    }
  };

  return (
    <div>
      <h2 className="dashboard-title">DNS Yönetimi</h2>

      {/* 1) Basit Domain Engelleme */}
      <div className="card mb-3">
        <div className="card-header">Domain Engelleme</div>
        <div className="card-body">
          <form onSubmit={handleAddDomain} className="mb-3">
            <div className="mb-3">
              <label>Domain</label>
              <input
                className="form-control"
                value={newDomain}
                onChange={(e) => setNewDomain(e.target.value)}
                placeholder="örn: facebook.com"
              />
            </div>
            <div className="form-check mb-2">
              <input
                type="checkbox"
                className="form-check-input"
                id="chkWildcard"
                checked={useWildcard}
                onChange={(e) => setUseWildcard(e.target.checked)}
              />
              <label className="form-check-label" htmlFor="chkWildcard">
                Alt alan adları da engellensin (wildcard) (*.example.com)
              </label>
            </div>
            <div className="mb-3">
              <label>Not (opsiyonel)</label>
              <input
                className="form-control"
                value={note}
                onChange={(e) => setNote(e.target.value)}
              />
            </div>
            <button type="submit" className="btn btn-primary">
              Engelle
            </button>
          </form>

          {/* Tablo: Domainler */}
          <div className="d-flex mb-2">
            {/* Toplu Seçim Butonu */}
            <button
              className="btn btn-danger me-2"
              onClick={handleDeleteSelected}
            >
              Seçilenleri Sil
            </button>
            {/* "Tümünü Seç" checkbox */}
            <div className="form-check align-self-center">
              <input
                type="checkbox"
                className="form-check-input"
                id="selectAll"
                // eğer tablo satırlarının sayısı == selectedDomains sayısı -> checked
                checked={domains.length > 0 && selectedDomains.length === domains.length}
                onChange={handleSelectAll}
              />
              <label className="form-check-label ms-1" htmlFor="selectAll">
                Tümünü Seç
              </label>
            </div>
          </div>

          <table className="table table-sm">
            <thead>
              <tr>
                <th>#</th>
                <th>Domain</th>
                <th>Wildcard?</th>
                <th>Not</th>
                <th>İşlem</th>
              </tr>
            </thead>
            <tbody>
              {domains.map((d, idx) => (
                <tr key={d._id}>
                  <td>
                    {/* satır checkbox */}
                    <input
                      type="checkbox"
                      className="form-check-input"
                      checked={selectedDomains.includes(d.domain)}
                      onChange={() => handleCheckboxChange(d)}
                    />
                    <span className="ms-2">{idx + 1}</span>
                  </td>
                  <td>{d.domain}</td>
                  <td>{d.use_wildcard ? "Evet" : "Hayır"}</td>
                  <td>{d.note || "-"}</td>
                  <td>
                    <button
                      className="btn btn-sm btn-secondary me-2"
                      onClick={() => handleDeleteDomain(d)}
                    >
                      Sil (tekli)
                    </button>
                  </td>
                </tr>
              ))}
              {domains.length === 0 && (
                <tr>
                  <td colSpan="5" className="text-center">
                    Henüz engelli domain yok.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* 2) Adblock Liste Ekleme */}
      <div className="card mb-3">
        <div className="card-header">Reklam/Tracker Liste Ekle</div>
        <div className="card-body">
          <p>
            Binlerce domaini topluca engellemek için bir liste URL'si
            girebilirsiniz. Örn: https://someone/adblock.txt
          </p>
          <div className="d-flex mb-3">
            <input
              className="form-control me-2"
              placeholder="AdBlock Liste URL'si"
              value={adblockUrl}
              onChange={(e) => setAdblockUrl(e.target.value)}
            />
            <button className="btn btn-primary" onClick={handleAdblockImport}>
              İndir ve Ekle
            </button>
          </div>
          {adblockStatus && <div className="alert alert-info">{adblockStatus}</div>}
        </div>
      </div>

      {/* DNS over HTTPS / advanced tips */}
      <div className="card mb-3">
        <div className="card-header">DNS Over HTTPS (DoH) Engelleme</div>
        <div className="card-body">
          <p>
            Modern tarayıcılar DoH kullanarak yerel DNS engellemeleri baypas edebilir.
            Bunu engellemek için SNI inspection veya IP tabanlı engel gerekebilir.
          </p>
          <ul>
            <li>Bilinen DoH sunucularının IP'lerini firewall üzerinden engelleyin.</li>
            <li>Kullanıcı tarayıcılarında "DNS over HTTPS" devre dışı bırakın.</li>
            <li>Daha gelişmiş TLS interception/inspection teknikleri uygulayın.</li>
          </ul>
        </div>
      </div>

      {/* DNS Sunucusu Forwarder */}
      <div className="card">
        <div className="card-header">Yerleşik DNS Sunucusu</div>
        <div className="card-body">
          <p>
            /etc/hosts veya basit conf yaklaşımları sınırlı olabilir.
            dnsmasq/bind9/Technitium gibi bir DNS sunucusu kullanarak
            "blocked_domains" tablosundaki engelli siteleri 127.0.0.1'e
            yönlendirebilir, diğer domainleri normal forward edebilirsiniz.
          </p>
          <p>
            Böylece Pi-hole benzeri bir reklam/izleyici engelleme sistemi elde edersiniz.
          </p>
        </div>
      </div>
    </div>
  );
}

export default DNSManagementPage;
