import React, { useState, useEffect } from 'react';
import axios from 'axios';

function LogsPage() {
  const [logs, setLogs] = useState([]);
  const [level, setLevel] = useState("");
  const [ipFilter, setIpFilter] = useState("");

  const fetchLogs = async () => {
    try {
      const token = localStorage.getItem("token");
      const params = {};
      if (level) params.level = level;
      if (ipFilter) params.ip = ipFilter;
      const resp = await axios.get("/api/logs/", {
        headers: { Authorization: `Bearer ${token}` },
        params
      });
      setLogs(resp.data);
    } catch (error) {
      alert("Log listesi alınamadı.");
    }
  };

  useEffect(() => {
    fetchLogs();
  }, []);

  return (
    <div style={{ margin: 20 }}>
      <h2>Log Yönetimi</h2>
      <div>
        <select value={level} onChange={(e) => setLevel(e.target.value)}>
          <option value="">Hepsi</option>
          <option value="ERROR">ERROR</option>
          <option value="WARN">WARN</option>
          <option value="INFO">INFO</option>
        </select>
        <input
          placeholder="IP filtre"
          value={ipFilter}
          onChange={(e) => setIpFilter(e.target.value)}
        />
        <button onClick={fetchLogs}>Filtre Uygula</button>
      </div>
      <hr/>
      <ul>
        {logs.map((log) => (
          <li key={log.id || log._id}>
            [{log.level}] {log.timestamp} - {log.message} (IP: {log.source_ip})
          </li>
        ))}
      </ul>
    </div>
  );
}

export default LogsPage;
