import React, { useState, useEffect } from 'react';
import axios from 'axios';

function FirewallRulesPage() {
  const [rules, setRules] = useState([]);
  const [ruleName, setRuleName] = useState("");
  const [sourceIp, setSourceIp] = useState("");
  const [action, setAction] = useState("DENY");

  const fetchRules = async () => {
    try {
      const token = localStorage.getItem("token");
      const resp = await axios.get("/api/firewall/rules", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setRules(resp.data);
    } catch (error) {
      console.error(error);
      alert("Kural listesi alınamadı!");
    }
  };

  const createRule = async () => {
    try {
      const token = localStorage.getItem("token");
      await axios.post("/api/firewall/rules", {
        rule_name: ruleName,
        source_ip: sourceIp,
        action
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert("Kural eklendi!");
      fetchRules();
    } catch (error) {
      alert("Kural eklenemedi: " + (error.response?.data?.detail || error.message));
    }
  };

  useEffect(() => {
    fetchRules();
  }, []);

  return (
    <div style={{ margin: 20 }}>
      <h2>Firewall Kural Yönetimi</h2>
      <div>
        <input
          placeholder="Kural İsmi"
          value={ruleName}
          onChange={(e) => setRuleName(e.target.value)}
        />
        <input
          placeholder="Kaynak IP"
          value={sourceIp}
          onChange={(e) => setSourceIp(e.target.value)}
        />
        <select value={action} onChange={(e) => setAction(e.target.value)}>
          <option value="DENY">DENY</option>
          <option value="ALLOW">ALLOW</option>
        </select>
        <button onClick={createRule}>Ekle</button>
      </div>
      <hr />
      <ul>
        {rules.map((r) => (
          <li key={r._id || r.id}>
            [{r.action}] {r.rule_name} - {r.source_ip}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default FirewallRulesPage;
