// src/pages/LoginPage.jsx
import React, { useState } from 'react';
import axios from 'axios';
// Örnek: react-router-dom'u kullanarak login sonrası yönlendirme yapacaksanız
import { useNavigate } from 'react-router-dom';

function LoginPage() {
  const navigate = useNavigate();  // eğer yönlendirme yapacaksanız

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async () => {
    try {
      // Burada backend'e istek atarsınız
      // Örneğin:
      // const response = await axios.post("/api/auth/login", {
      //   username,
      //   password
      // });
      // localStorage.setItem("token", response.data.access_token);

      alert("Giriş başarılı! (Demo)");

      // yönlendirme yapabilirsiniz
      navigate("/");
    } catch (error) {
      alert("Giriş hatası: " + (error.response?.data?.detail || error.message));
    }
  };

  return (
    <div style={styles.container}>
      {/* Sol Panel (Kullanıcı Adı / Parola / Logo vs.) */}
      <div style={styles.leftPanel}>
        <div style={styles.leftContentWrapper}>
          <h2 style={{ color: '#fff', marginBottom: '1.5rem' }}>NetGate</h2>

          <label style={styles.label}>Kullanıcı Adı</label>
          <input
            type="text"
            style={styles.input}
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />

          <label style={styles.label}>Parola</label>
          <input
            type="password"
            style={styles.input}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />

          <button style={styles.loginButton} onClick={handleLogin}>
            Login
          </button>

          <div style={{ marginTop: '2rem', color: 'rgba(255,255,255,0.6)', fontSize: '0.8rem' }}>
            © 2025 NetGate Inc.
          </div>
        </div>
      </div>

      {/* Sağ Panel (Arka plan görseli veya düz renk + Karşılama metni) */}
      <div style={styles.rightPanel}>
        <div style={styles.rightOverlay}>
          <h1 style={{ fontSize: '2rem', color: '#fff', marginBottom: '0.5rem' }}>
            Welcome to NetGate Firewall
          </h1>
          <p style={{ color: '#ddd', maxWidth: '400px', textAlign: 'center' }}>
            Güvenli bağlantı, gelişmiş denetim ve gerçek zamanlı tehdit koruması için tasarlanan
            yeni nesil firewall yönetim paneline hoş geldiniz.
          </p>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;

/* Inline CSS Stillerini Nesne Olarak Tanımlayalım */
const styles = {
  container: {
    display: 'flex',
    flexDirection: 'row',
    height: '100vh',
    width: '100vw',
  },
  leftPanel: {
    flex: '0 0 35%',  // Sol panel genişliği
    backgroundColor: '#0A3C5F',  // Koyu mavi ton
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  leftContentWrapper: {
    width: '80%',
    maxWidth: '300px',
    display: 'flex',
    flexDirection: 'column',
  },
  label: {
    color: '#fff',
    marginBottom: '0.2rem',
    fontWeight: '500',
  },
  input: {
    marginBottom: '1rem',
    padding: '0.5rem',
    borderRadius: '4px',
    border: '1px solid #ccc',
    fontSize: '1rem',
  },
  loginButton: {
    backgroundColor: '#198754',
    color: '#fff',
    padding: '0.6rem',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '1rem',
    fontWeight: '600',
  },
  rightPanel: {
    flex: 1,
    // Örnek olarak bir arka plan görseli ekleyebilirsiniz
    // backgroundImage: 'url("/images/firewall-bg.jpg")',
    // backgroundSize: 'cover',
    // backgroundPosition: 'center',
    backgroundColor: '#0069D9',  // Renk ya da degrade
    position: 'relative',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  rightOverlay: {
    textAlign: 'center',
    padding: '2rem',
    backgroundColor: 'rgba(0,0,0,0.3)',
    borderRadius: '8px',
  },
};
