// src/pages/LoginPage.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function LoginPage() {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async () => {
    try {
      // const response = await axios.post("/api/auth/login", { username, password });
      // localStorage.setItem("token", response.data.access_token);
      alert("Giriş başarılı (Demo)!");
      navigate("/");  // Giriş başarılıysa ana sayfaya yönlendir
    } catch (error) {
      alert("Giriş hatası: " + (error.response?.data?.detail || error.message));
    }
  };

  return (
    <div style={styles.container}>
      {/* SOL KISIM */}
      <div style={styles.leftSide}>
        <div style={styles.leftSideContent}>
          <h2 style={{ color: '#fff', marginBottom: '2rem' }}>NetGate Firewall</h2>

          <label style={styles.label}>Kullanıcı Adı</label>
          <input
            style={styles.input}
            type="text"
            placeholder="admin"
            value={username}
            onChange={e => setUsername(e.target.value)}
          />

          <label style={styles.label}>Parola</label>
          <input
            style={styles.input}
            type="password"
            placeholder="••••••"
            value={password}
            onChange={e => setPassword(e.target.value)}
          />

          <button style={styles.loginBtn} onClick={handleLogin}>
            Giriş
          </button>

          <div style={styles.footerText}>
            © 2025 NetGate Inc.
          </div>
        </div>
      </div>

      {/* SAĞ KISIM */}
      <div style={styles.rightSide}>
        {/* İsterseniz bir arka plan resmi ekleyebilirsiniz:
            backgroundImage: 'url("/assets/login-bg.jpg")', backgroundSize: 'cover', ...
        */}
        <div style={styles.rightOverlay}>
          <h1 style={{ color: '#fff', fontSize: '2rem', marginBottom: '1rem' }}>Hoş Geldiniz</h1>
          <p style={{ color: '#eee', maxWidth: '350px', textAlign: 'center', lineHeight: '1.4' }}>
            NetGate Firewall Yönetim Paneline hoş geldiniz. Güvenli ağ yönetimi
            ve gerçek zamanlı tehdit koruması için oturum açın.
          </p>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;


/* ============ STYLES (Inline) ============ */
const styles = {
  container: {
    display: 'flex',
    width: '100vw',
    height: '100vh',
    flexDirection: 'row'
  },
  leftSide: {
    flex: 1,
    background: 'linear-gradient(180deg, #0B3861 0%, #043263 100%)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },
  leftSideContent: {
    width: '80%',
    maxWidth: '300px',
    color: '#fff'
  },
  label: {
    display: 'block',
    marginBottom: '5px',
    marginTop: '15px',
    fontWeight: 'bold'
  },
  input: {
    width: '100%',
    padding: '0.5rem',
    borderRadius: '4px',
    border: '1px solid #ccc'
  },
  loginBtn: {
    width: '100%',
    padding: '0.7rem',
    marginTop: '20px',
    border: 'none',
    borderRadius: '4px',
    backgroundColor: '#FFC107', // Sarımsı bir renk
    color: '#333',
    fontWeight: 'bold',
    cursor: 'pointer'
  },
  footerText: {
    marginTop: '2rem',
    fontSize: '0.8rem',
    color: 'rgba(255,255,255,0.6)',
    textAlign: 'center'
  },
  rightSide: {
    flex: 1.3,
    backgroundColor: '#0A5290',
    position: 'relative',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },
  rightOverlay: {
    backgroundColor: 'rgba(0,0,0,0.3)',
    padding: '2rem 3rem',
    borderRadius: '8px',
    textAlign: 'center'
  }
};
