// src/pages/FirewallRulesPage.jsx
import React, { useState } from 'react';

function FirewallRulesPage() {
  // Basit state örneği
  const [newRuleName, setNewRuleName] = useState("");
  const [newRuleType, setNewRuleType] = useState("IP Yasaklama");
  const [newPriority, setNewPriority] = useState(3);
  const [newDesc, setNewDesc] = useState("");

  // Örnek kural listesi
  const [rules, setRules] = useState([
    { id: 1, name: "SSH Engelleme", type: "Port Kısıtlama", priority: 2, isActive: true },
    { id: 2, name: "IP Karaliste", type: "IP Yasaklama", priority: 1, isActive: false },
  ]);

  const createNewRule = () => {
    const newId = Math.floor(Math.random() * 10000);
    const newRule = {
      id: newId,
      name: newRuleName,
      type: newRuleType,
      priority: newPriority,
      desc: newDesc,
      isActive: true,
    };
    setRules([...rules, newRule]);
    // Sıfırla
    setNewRuleName("");
    setNewRuleType("IP Yasaklama");
    setNewPriority(3);
    setNewDesc("");
  };

  return (
    <div>
      <h2 className="mb-4">Güvenlik Kuralları</h2>

      {/* Kural Tablosu */}
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Kural Adı</th>
            <th>Tipi</th>
            <th>Öncelik</th>
            <th>Durum</th>
            <th>İşlem</th>
          </tr>
        </thead>
        <tbody>
          {rules.map(rule => (
            <tr key={rule.id}>
              <td>{rule.name}</td>
              <td>{rule.type}</td>
              <td>{rule.priority}</td>
              <td>{rule.isActive ? "Aktif" : "Pasif"}</td>
              <td>
                <button className="btn btn-sm btn-primary me-2">Düzenle</button>
                {rule.isActive ? (
                  <button className="btn btn-sm btn-warning">Pasifleştir</button>
                ) : (
                  <button className="btn btn-sm btn-success">Aktifleştir</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Yeni Kural Ekle */}
      <h5 className="mt-4">Yeni Kural Ekle</h5>
      <div className="mb-3">
        <label className="form-label">Kural Adı:</label>
        <input
          type="text"
          className="form-control"
          value={newRuleName}
          onChange={(e) => setNewRuleName(e.target.value)}
        />
      </div>
      <div className="mb-3">
        <label className="form-label">Tipi:</label>
        <select
          className="form-select"
          value={newRuleType}
          onChange={(e) => setNewRuleType(e.target.value)}>
          <option>IP Yasaklama</option>
          <option>Port Kısıtlama</option>
          <option>Protokol Engelleme</option>
        </select>
      </div>
      <div className="mb-3">
        <label className="form-label">Öncelik:</label>
        <input
          type="number"
          className="form-control"
          value={newPriority}
          onChange={(e) => setNewPriority(e.target.value)}
        />
      </div>
      <div className="mb-3">
        <label className="form-label">Açıklama:</label>
        <textarea
          className="form-control"
          rows={2}
          value={newDesc}
          onChange={(e) => setNewDesc(e.target.value)}
        />
      </div>
      <button className="btn btn-primary" onClick={createNewRule}>
        Kaydet
      </button>
    </div>
  );
}

export default FirewallRulesPage;
