// File: src/pages/FirewallRulesPage.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";

// 1) react-beautiful-dnd importları
import {
  DragDropContext,
  Droppable,
  Draggable
} from "react-beautiful-dnd";

function FirewallRulesPage() {
  // Kuralların listesi
  const [rules, setRules] = useState([]);

  // Form state
  const [ruleId, setRuleId] = useState(null);
  const [ruleName, setRuleName] = useState("");
  const [sourceIps, setSourceIps] = useState("");
  const [port, setPort] = useState("");
  const [protocol, setProtocol] = useState("TCP");
  const [action, setAction] = useState("ALLOW");
  const [direction, setDirection] = useState("IN");
  const [profile, setProfile] = useState("Any");
  const [description, setDescription] = useState("");

  const [enabled, setEnabled] = useState(true);
  const [priority, setPriority] = useState(100);
  const [scheduleStart, setScheduleStart] = useState("");
  const [scheduleEnd, setScheduleEnd] = useState("");
  const [daysOfWeek, setDaysOfWeek] = useState([]); // array of number (0..6)

  // Edit mode
  const isEditMode = ruleId !== null;

  // 2) Kuralları yükle
  useEffect(() => {
    fetchRules();
  }, []);

  const fetchRules = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://127.0.0.1:8000/firewall/rules", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setRules(res.data);
    } catch (err) {
      alert("Kural listesi alınamadı: " + (err.response?.data?.detail || err.message));
    }
  };

  // 3) Formu sıfırla
  const clearForm = () => {
    setRuleId(null);
    setRuleName("");
    setSourceIps("");
    setPort("");
    setProtocol("TCP");
    setAction("ALLOW");
    setDirection("IN");
    setProfile("Any");
    setDescription("");
    setEnabled(true);
    setPriority(100);
    setScheduleStart("");
    setScheduleEnd("");
    setDaysOfWeek([]);
  };

  // 4) Kural submit (create/update)
  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    // virgülle ayrılan IP’leri diziye çevir
    const ipsArray = sourceIps
      .split(",")
      .map((ip) => ip.trim())
      .filter((ip) => ip !== "");

    const body = {
      rule_name: ruleName,
      source_ips: ipsArray.length ? ipsArray : null,
      port: port ? Number(port) : null,
      protocol,
      action,
      direction,
      profile,
      description: description || null,
      enabled,
      priority: Number(priority),
      schedule_start: scheduleStart || null,
      schedule_end: scheduleEnd || null,
      days_of_week: daysOfWeek.map((d) => parseInt(d, 10))
    };

    try {
      let resp;
      if (!isEditMode) {
        // create
        resp = await axios.post("http://127.0.0.1:8000/firewall/rules", body, {
          headers: { Authorization: `Bearer ${token}` }
        });
        alert(resp.data.message || "Kural eklendi");
      } else {
        // update
        resp = await axios.put(`http://127.0.0.1:8000/firewall/rules/${ruleId}`, body, {
          headers: { Authorization: `Bearer ${token}` }
        });
        alert(resp.data.message || "Kural güncellendi");
      }
      clearForm();
      fetchRules(); // tabloyu tazele
    } catch (err) {
      alert("Hata oluştu: " + (err.response?.data?.detail || err.message));
    }
  };

  // 5) Kural düzenleme
  const handleEditClick = (rule) => {
    setRuleId(rule._id || null);
    setRuleName(rule.rule_name || "");
    if (rule.source_ips && rule.source_ips.length > 0) {
      setSourceIps(rule.source_ips.join(","));
    } else {
      setSourceIps("");
    }
    setPort(rule.port || "");
    setProtocol(rule.protocol || "TCP");
    setAction(rule.action || "ALLOW");
    setDirection(rule.direction || "IN");
    setProfile(rule.profile || "Any");
    setDescription(rule.description || "");

    setEnabled(rule.enabled !== false);
    setPriority(rule.priority || 100);
    setScheduleStart(rule.schedule_start || "");
    setScheduleEnd(rule.schedule_end || "");
    setDaysOfWeek(rule.days_of_week || []);
  };

  // 6) Kural silme
  const handleDelete = async (id) => {
    if (!window.confirm("Bu kuralı silmek istediğinize emin misiniz?")) return;
    try {
      const token = localStorage.getItem("token");
      const resp = await axios.delete(`http://127.0.0.1:8000/firewall/rules/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert(resp.data.message || "Kural silindi");
      fetchRules();
    } catch (err) {
      alert("Silme hatası: " + (err.response?.data?.detail || err.message));
    }
  };

  // 7) Haftanın günleri (checkbox)
  const handleDayCheck = (dayIndex) => {
    if (daysOfWeek.includes(dayIndex)) {
      setDaysOfWeek(daysOfWeek.filter((d) => d !== dayIndex));
    } else {
      setDaysOfWeek([...daysOfWeek, dayIndex]);
    }
  };

  // 8) Sürükle-bırak onDragEnd event
  const onDragEnd = async (result) => {
    if (!result.destination) return;
    const { source, destination } = result;

    // Listeyi kopyala
    const newRules = Array.from(rules);
    // item'ı (kural) al
    const [movedItem] = newRules.splice(source.index, 1);
    newRules.splice(destination.index, 0, movedItem);

    // newRules içindeki sıralamayı state'e kaydet
    setRules(newRules);

    // (İsteğe Bağlı) Sunucuya priority güncellemesi:
    // moveItem'a newPriority=destination.index vs. verip PUT atmak
    // Bu aşamada, her item'a sırasına göre priority verip
    // arka planda birer PUT isteği atabilirsiniz. Örnek:
    /*
    try {
      const token = localStorage.getItem("token");
      // Tek seferde hepsini PUT edebilirsiniz veya
      // movedItem'a PUT atabilirsiniz
      for (let i = 0; i < newRules.length; i++) {
        const updatedItem = { ...newRules[i], priority: i + 1 };
        await axios.put(`http://127.0.0.1:8000/firewall/rules/${updatedItem._id}`, updatedItem, {
          headers: { Authorization: `Bearer ${token}` }
        });
      }
      alert("Priority güncellendi!");
    } catch (err) {
      console.error(err);
      alert("Priority güncelleme hatası: " + err);
    }
    */
  };

  return (
    <div>
      <h2 className="dashboard-title">Firewall Kuralları</h2>

      {/* Sürükle-bırak konteyner */}
      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="firewall-rules-table">
          {(provided) => (
            <div
              className="card mb-4"
              ref={provided.innerRef}
              {...provided.droppableProps}
            >
              <div className="card-header">Mevcut Kurallar</div>
              <div className="card-body p-0">
                <table className="table mb-0">
                  <thead>
                    <tr>
                      <th>#</th>
                      <th>Kural Adı</th>
                      <th>Kaynak IP(ler)</th>
                      <th>Port</th>
                      <th>Action</th>
                      <th>Direction</th>
                      <th>Öncelik</th>
                      <th>Etkin?</th>
                      <th>İşlem</th>
                    </tr>
                  </thead>
                  <tbody>
                    {rules.map((r, index) => (
                      <Draggable
                        key={r._id}
                        draggableId={r._id}
                        index={index}
                      >
                        {(providedRow) => (
                          <tr
                            ref={providedRow.innerRef}
                            {...providedRow.draggableProps}
                            {...providedRow.dragHandleProps}
                          >
                            <td>{index + 1}</td>
                            <td>{r.rule_name}</td>
                            <td>
                              {r.source_ips && r.source_ips.length
                                ? r.source_ips.join(", ")
                                : "-"}
                            </td>
                            <td>{r.port || "-"}</td>
                            <td>{r.action}</td>
                            <td>{r.direction}</td>
                            <td>{r.priority}</td>
                            <td>{r.enabled ? "Aktif" : "Pasif"}</td>
                            <td>
                              <button
                                className="btn btn-sm btn-primary me-2"
                                onClick={() => handleEditClick(r)}
                              >
                                Düzenle
                              </button>
                              <button
                                className="btn btn-sm btn-danger"
                                onClick={() => handleDelete(r._id)}
                              >
                                Sil
                              </button>
                            </td>
                          </tr>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </Droppable>
      </DragDropContext>

      {/* Form */}
      <div className="card">
        <div className="card-header">
          {isEditMode ? "Kural Düzenle" : "Yeni Kural Ekle"}
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label className="form-label">Kural Adı</label>
              <input
                className="form-control"
                required
                value={ruleName}
                onChange={(e) => setRuleName(e.target.value)}
              />
            </div>

            <div className="mb-3">
              <label>Kaynak IP(ler) (virgülle ayır)</label>
              <input
                className="form-control"
                value={sourceIps}
                onChange={(e) => setSourceIps(e.target.value)}
                placeholder="örn: 192.168.1.10, 10.0.0.5"
              />
            </div>

            <div className="mb-3">
              <label>Port (opsiyonel)</label>
              <input
                type="number"
                className="form-control"
                value={port}
                onChange={(e) => setPort(e.target.value)}
              />
            </div>

            <div className="mb-3">
              <label>Protocol</label>
              <select
                className="form-select"
                value={protocol}
                onChange={(e) => setProtocol(e.target.value)}
              >
                <option value="TCP">TCP</option>
                <option value="UDP">UDP</option>
              </select>
            </div>

            <div className="mb-3">
              <label>Action</label>
              <select
                className="form-select"
                value={action}
                onChange={(e) => setAction(e.target.value)}
              >
                <option value="ALLOW">ALLOW</option>
                <option value="DENY">DENY</option>
              </select>
            </div>

            <div className="mb-3">
              <label>Direction</label>
              <select
                className="form-select"
                value={direction}
                onChange={(e) => setDirection(e.target.value)}
              >
                <option value="IN">IN (Inbound)</option>
                <option value="OUT">OUT (Outbound)</option>
              </select>
            </div>

            <div className="mb-3">
              <label>Profile</label>
              <select
                className="form-select"
                value={profile}
                onChange={(e) => setProfile(e.target.value)}
              >
                <option value="Any">Any</option>
                <option value="Domain">Domain</option>
                <option value="Private">Private</option>
                <option value="Public">Public</option>
              </select>
            </div>

            <div className="mb-3">
              <label>Açıklama (opsiyonel)</label>
              <input
                className="form-control"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              />
            </div>

            {/* Yeni Alanlar */}
            <div className="mb-3 form-check">
              <input
                type="checkbox"
                className="form-check-input"
                checked={enabled}
                onChange={(e) => setEnabled(e.target.checked)}
                id="chkEnabled"
              />
              <label className="form-check-label" htmlFor="chkEnabled">
                Kural Etkin (Enabled)
              </label>
            </div>

            <div className="mb-3">
              <label>Öncelik (Priority)</label>
              <input
                type="number"
                className="form-control"
                value={priority}
                onChange={(e) => setPriority(e.target.value)}
              />
            </div>

            <div className="mb-3">
              <label>Zaman Başlangıcı (HH:MM)</label>
              <input
                type="time"
                className="form-control"
                value={scheduleStart}
                onChange={(e) => setScheduleStart(e.target.value)}
              />
            </div>

            <div className="mb-3">
              <label>Zaman Sonu (HH:MM)</label>
              <input
                type="time"
                className="form-control"
                value={scheduleEnd}
                onChange={(e) => setScheduleEnd(e.target.value)}
              />
            </div>

            <div className="mb-3">
              <label>Haftanın Günleri</label>
              <div>
                {["Pzt", "Sal", "Çar", "Per", "Cum", "Cmt", "Paz"].map((label, idx) => (
                  <div key={idx} className="form-check form-check-inline">
                    <input
                      type="checkbox"
                      className="form-check-input"
                      checked={daysOfWeek.includes(idx)}
                      onChange={() => handleDayCheck(idx)}
                      id={`chkDay${idx}`}
                    />
                    <label htmlFor={`chkDay${idx}`} className="form-check-label">
                      {label}
                    </label>
                  </div>
                ))}
              </div>
            </div>

            <button type="submit" className="btn btn-primary">
              {isEditMode ? "Güncelle" : "Ekle"}
            </button>
            {isEditMode && (
              <button type="button" className="btn btn-secondary ms-2" onClick={clearForm}>
                Vazgeç
              </button>
            )}
          </form>
        </div>
      </div>
    </div>
  );
}

export default FirewallRulesPage;
