// src/pages/LoginPage.jsx
import React, { useState } from "react";
import axios from "axios";

function LoginPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async (event) => {
    event.preventDefault();  // Formun geleneksel davranışını engelle
    console.log("handleLogin triggered, username =", username, "password =", password);

    try {
      const res = await axios.post("http://127.0.0.1:8000/auth/login", {
        username,
        password
      });
      console.log("Login response data:", res.data);

      if (res.data.access_token) {
        localStorage.setItem("token", res.data.access_token);
        alert("Giriş başarılı!");
        window.location.href = "/";
      } else {
        alert("Sunucu token döndürmedi, lütfen kontrol edin.");
      }
    } catch (err) {
      console.error("Login error:", err);
      alert("Giriş hatası: " + (err.response?.data?.detail || err.message));
    }
  };

  console.log("LoginPage loaded!"); // test purpose

  return (
    <div className="d-flex justify-content-center align-items-center vh-100" style={{ backgroundColor: "#f3f4f6" }}>
      <div className="card shadow" style={{ width: "420px" }}>
        <div className="card-header text-center bg-primary text-white">
          <h4 className="mb-0">NetGate Firewall Login</h4>
        </div>
        <div className="card-body">
          {/* ÖNEMLİ: <form onSubmit={handleLogin}> ve <button type="submit"> */}
          <form onSubmit={handleLogin}>
            <div className="mb-3">
              <label className="form-label">Kullanıcı Adı</label>
              <input
                type="text"
                className="form-control"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Parola</label>
              <input
                type="password"
                className="form-control"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <button type="submit" className="btn btn-primary w-100">
              Giriş
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
