// File: src/pages/DNSManagementPage.jsx

import React, { useState, useEffect } from "react";
import axios from "axios";

function DNSManagementPage() {
  // 1) Basit Domain Engelleme
  const [domains, setDomains] = useState([]);
  const [newDomain, setNewDomain] = useState("");
  const [useWildcard, setUseWildcard] = useState(true);
  const [note, setNote] = useState("");

  // 2) Adblock Liste Ekleme
  const [adblockUrl, setAdblockUrl] = useState("");
  const [adblockStatus, setAdblockStatus] = useState("");

  // Çoklu seçim için state
  const [selectedIds, setSelectedIds] = useState([]);

  // Geri bildirim (DNS Over HTTPS engelleme vb.)
  const [dohBlockStatus, setDohBlockStatus] = useState("");

  useEffect(() => {
    fetchDomains();
  }, []);

  const fetchDomains = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://127.0.0.1:8000/dns/domains", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setDomains(res.data);
      // Seçimleri sıfırla
      setSelectedIds([]);
    } catch (err) {
      alert("Domain listesi çekilemedi: " + err.message);
    }
  };

  const handleAddDomain = async (e) => {
    e.preventDefault();
    if (!newDomain) {
      alert("Lütfen engellenecek domaini girin!");
      return;
    }
    try {
      const token = localStorage.getItem("token");
      const body = {
        domain: newDomain.trim().toLowerCase(),
        use_wildcard: useWildcard,
        note
      };
      const res = await axios.post("http://127.0.0.1:8000/dns/domains", body, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert(res.data.message || "Domain engellendi.");
      setNewDomain("");
      setUseWildcard(true);
      setNote("");
      fetchDomains();
    } catch (err) {
      alert("Domain ekleme hatası: " + (err.response?.data?.detail || err.message));
    }
  };

  // Tekli silme (id bazlı)
  const handleDeleteDomain = async (domainObj) => {
    if (!window.confirm(`"${domainObj.domain}" kaydını silmek istiyor musunuz?`)) return;
    try {
      const token = localStorage.getItem("token");
      const url = `http://127.0.0.1:8000/dns/domains/${domainObj._id}`;
      const res = await axios.delete(url, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert(res.data.message || "Silindi");
      fetchDomains();
    } catch (err) {
      alert("Silme hatası: " + (err.response?.data?.detail || err.message));
    }
  };

  // Checkbox seçimi handle
  const handleCheckboxChange = (domainObj) => {
    const id = domainObj._id;
    if (selectedIds.includes(id)) {
      setSelectedIds(selectedIds.filter((x) => x !== id));
    } else {
      setSelectedIds([...selectedIds, id]);
    }
  };

  // Tümünü seç
  const handleSelectAll = (e) => {
    if (e.target.checked) {
      const allIds = domains.map((d) => d._id);
      setSelectedIds(allIds);
    } else {
      setSelectedIds([]);
    }
  };

  // Çoklu silme
  const handleDeleteSelected = async () => {
    if (selectedIds.length === 0) {
      alert("Seçili hiçbir kayıt yok!");
      return;
    }
    if (!window.confirm(`Seçili ${selectedIds.length} domaini silmek istiyor musunuz?`)) {
      return;
    }
    try {
      const token = localStorage.getItem("token");
      for (const id of selectedIds) {
        const url = `http://127.0.0.1:8000/dns/domains/${id}`;
        await axios.delete(url, {
          headers: { Authorization: `Bearer ${token}` },
        });
      }
      alert("Seçilen domainler silindi.");
      fetchDomains();
    } catch (err) {
      alert("Toplu silme hatası: " + (err.response?.data?.detail || err.message));
    }
  };

  // Adblock liste import
  const handleAdblockImport = async () => {
    if (!adblockUrl) {
      alert("Lütfen AdBlock liste URL'si girin!");
      return;
    }
    try {
      const token = localStorage.getItem("token");
      setAdblockStatus("İndiriliyor...");
      const body = { url: adblockUrl };
      const res = await axios.post("http://127.0.0.1:8000/dns/adblocklist", body, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAdblockStatus(res.data.message || "Liste eklendi");
      fetchDomains();
    } catch (err) {
      setAdblockStatus("Hata: " + (err.response?.data?.detail || err.message));
    }
  };

  // DNS Over HTTPS Engelle butonu
  const handleBlockDoH = async () => {
    try {
      setDohBlockStatus("DoH sunucuları engelleniyor...");
      const token = localStorage.getItem("token");
      const res = await axios.post("http://127.0.0.1:8000/dns/doh-block", null, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setDohBlockStatus(res.data.message || "DoH IP’ler engellendi");
    } catch (err) {
      setDohBlockStatus("Hata: " + (err.response?.data?.detail || err.message));
    }
  };

  return (
    <div>
      <h2 className="dashboard-title">DNS Yönetimi</h2>

      {/* 1) Basit Domain Engelleme */}
      <div className="card mb-3">
        <div className="card-header">Domain Engelleme</div>
        <div className="card-body">
          <form onSubmit={handleAddDomain} className="mb-3">
            <div className="mb-3">
              <label>Domain</label>
              <input
                className="form-control"
                value={newDomain}
                onChange={(e) => setNewDomain(e.target.value)}
                placeholder="örn: facebook.com"
              />
            </div>
            <div className="form-check mb-2">
              <input
                type="checkbox"
                className="form-check-input"
                id="chkWildcard"
                checked={useWildcard}
                onChange={(e) => setUseWildcard(e.target.checked)}
              />
              <label className="form-check-label" htmlFor="chkWildcard">
                Alt alan adları da engellensin (wildcard) (*.example.com)
              </label>
            </div>
            <div className="mb-3">
              <label>Not (opsiyonel)</label>
              <input
                className="form-control"
                value={note}
                onChange={(e) => setNote(e.target.value)}
              />
            </div>
            <button type="submit" className="btn btn-primary">
              Engelle
            </button>
          </form>

          {/* Toplu seçim araçları */}
          <div className="d-flex mb-2">
            <button
              className="btn btn-danger me-3"
              onClick={handleDeleteSelected}
            >
              Seçilenleri Sil
            </button>
            <div className="form-check">
              <input
                type="checkbox"
                className="form-check-input"
                id="selectAllDomains"
                checked={domains.length > 0 && selectedIds.length === domains.length}
                onChange={handleSelectAll}
              />
              <label className="form-check-label ms-1" htmlFor="selectAllDomains">
                Tümünü Seç
              </label>
            </div>
          </div>

          <table className="table table-sm">
            <thead>
              <tr>
                <th>#</th>
                <th>Domain</th>
                <th>Wildcard?</th>
                <th>Not</th>
                <th>İşlem</th>
              </tr>
            </thead>
            <tbody>
              {domains.map((item, idx) => (
                <tr key={item._id}>
                  <td>
                    <input
                      type="checkbox"
                      className="form-check-input me-2"
                      checked={selectedIds.includes(item._id)}
                      onChange={() => handleCheckboxChange(item)}
                    />
                    {idx + 1}
                  </td>
                  <td>{item.domain}</td>
                  <td>{item.use_wildcard ? "Evet" : "Hayır"}</td>
                  <td>{item.note || "-"}</td>
                  <td>
                    {/* Tekli silme butonu */}
                    <button
                      className="btn btn-sm btn-secondary"
                      onClick={() => handleDeleteDomain(item)}
                    >
                      Sil (tekli)
                    </button>
                  </td>
                </tr>
              ))}
              {domains.length === 0 && (
                <tr>
                  <td colSpan="5" className="text-center">
                    Henüz engelli domain yok.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* 2) Adblock Liste Ekleme */}
      <div className="card mb-3">
        <div className="card-header">Reklam/Tracker Liste Ekle</div>
        <div className="card-body">
          <p>
            Binlerce domaini topluca engellemek için bir liste URL'si girebilirsiniz.
            Örn: https://someone/adblock.txt
          </p>
          <div className="d-flex mb-3">
            <input
              className="form-control me-2"
              placeholder="AdBlock Liste URL'si"
              value={adblockUrl}
              onChange={(e) => setAdblockUrl(e.target.value)}
            />
            <button className="btn btn-primary" onClick={handleAdblockImport}>
              İndir ve Ekle
            </button>
          </div>
          {adblockStatus && (
            <div className="alert alert-info">{adblockStatus}</div>
          )}
        </div>
      </div>

      {/* 3) DNS Over HTTPS Engelle */}
      <div className="card mb-3">
        <div className="card-header">DNS Over HTTPS Engelle</div>
        <div className="card-body">
          <p>
            Modern tarayıcılar DoH (DNS over HTTPS) kullanarak yerel DNS engellemelerini
            baypas edebilir. Bu buton, bilinen DoH sunucularının IP’lerini firewall
            kurallarıyla <b>DENY</b> olarak ekler.
          </p>
          <button className="btn btn-warning" onClick={handleBlockDoH}>
            DNS over HTTPS Engelle
          </button>
          {dohBlockStatus && (
            <div className="mt-2 alert alert-info">{dohBlockStatus}</div>
          )}
        </div>
      </div>

      {/* 4) DNS Sunucusu Forwarder */}
      <div className="card">
        <div className="card-header">Yerleşik DNS Sunucusu</div>
        <div className="card-body">
          <p>
            /etc/hosts veya basit conf yaklaşımları sınırlı olabilir.
            dnsmasq/bind9/Technitium gibi bir DNS sunucusu kullanarak
            "blocked_domains" tablosundaki engelli siteleri 127.0.0.1'e
            yönlendirebilir, diğer domainleri normal forward edebilirsiniz.
          </p>
          <p>
            Böylece Pi-hole benzeri bir reklam/izleyici engelleme sistemi elde edersiniz.
          </p>
        </div>
      </div>
    </div>
  );
}

export default DNSManagementPage;
