// File: src/pages/DomainBlockPage.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";

function DomainBlockPage() {
  const [domains, setDomains] = useState([]);
  const [newDomain, setNewDomain] = useState("");
  const [note, setNote] = useState("");
  const [useWildcard, setUseWildcard] = useState(true);

  useEffect(() => {
    fetchDomains();
  }, []);

  const fetchDomains = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://127.0.0.1:8000/dns/domains", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setDomains(res.data);
    } catch (err) {
      alert("Domain listesi alınamadı: " + err.message);
    }
  };

  const handleAdd = async (e) => {
    e.preventDefault();
    if (!newDomain) {
      alert("Engellemek istediğiniz domaini giriniz!");
      return;
    }
    try {
      const token = localStorage.getItem("token");
      const body = { domain: newDomain, note, use_wildcard: useWildcard };
      const res = await axios.post("http://127.0.0.1:8000/dns/domains", body, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert(res.data.message || "Domain eklendi");
      setNewDomain("");
      setNote("");
      setUseWildcard(true);
      fetchDomains();
    } catch (err) {
      alert("Domain ekleme hatası: " + (err.response?.data?.detail || err.message));
    }
  };

  const handleDelete = async (dom) => {
    if (!window.confirm(`"${dom.domain}" engel listesinden çıkarılsın mı?`)) return;
    try {
      const token = localStorage.getItem("token");
      const url = `http://127.0.0.1:8000/dns/domains/${dom.domain}`;
      const res = await axios.delete(url, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert(res.data.message || "Silindi");
      fetchDomains();
    } catch (err) {
      alert("Silme hatası: " + (err.response?.data?.detail || err.message));
    }
  };

  return (
    <div>
      <h2 className="dashboard-title">DNS Tabanlı Engelleme</h2>

      <div className="card mb-3">
        <div className="card-header">Mevcut Engellenmiş Domainler</div>
        <div className="card-body p-0">
          <table className="table mb-0">
            <thead>
              <tr>
                <th>Domain</th>
                <th>Wildcard?</th>
                <th>Not</th>
                <th>İşlem</th>
              </tr>
            </thead>
            <tbody>
              {domains.map((d) => (
                <tr key={d._id}>
                  <td>{d.domain}</td>
                  <td>{d.use_wildcard ? "Evet" : "Hayır"}</td>
                  <td>{d.note || "-"}</td>
                  <td>
                    <button
                      className="btn btn-sm btn-danger"
                      onClick={() => handleDelete(d)}
                    >
                      Sil
                    </button>
                  </td>
                </tr>
              ))}
              {domains.length === 0 && (
                <tr>
                  <td colSpan="4" className="text-center p-3">
                    Henüz engelli domain yok.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      <div className="card">
        <div className="card-header">Domain Ekle</div>
        <div className="card-body">
          <form onSubmit={handleAdd}>
            <div className="mb-3">
              <label>Domain</label>
              <input
                className="form-control"
                value={newDomain}
                onChange={(e) => setNewDomain(e.target.value)}
                placeholder="örn: facebook.com"
              />
            </div>
            <div className="mb-3 form-check">
              <input
                type="checkbox"
                className="form-check-input"
                checked={useWildcard}
                onChange={(e) => setUseWildcard(e.target.checked)}
                id="chkWildcard"
              />
              <label htmlFor="chkWildcard" className="form-check-label">
                Alt alan adları da engellensin (wildcard)
              </label>
            </div>
            <div className="mb-3">
              <label>Not (opsiyonel)</label>
              <input
                className="form-control"
                value={note}
                onChange={(e) => setNote(e.target.value)}
                placeholder="Açıklama vs."
              />
            </div>
            <button type="submit" className="btn btn-primary">Ekle</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default DomainBlockPage;
