// src/App.js
import React from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Layout from './Layout';

// Sayfalar
import LoginPage from './pages/LoginPage';  // <-- Giriş sayfası
import DashboardPage from './pages/DashboardPage';
import LogsPage from './pages/LogsPage';
import FirewallRulesPage from './pages/FirewallRulesPage';
import UpdatesPage from './pages/UpdatesPage';
import ReportsPage from './pages/ReportsPage';
import SettingsPage from './pages/SettingsPage';

function App() {
  return (
    <BrowserRouter>
      <Routes>
        {/*
          /login için Layout olmadan doğrudan LoginPage gösteriyoruz.
          Böylece login sırasında yan menü veya üst çubuk görünmez.
        */}
        <Route path="/login" element={<LoginPage />} />

        {/* Anasayfa ve diğer rotalar Layout içinde */}
        <Route
          path="/"
          element={
            <Layout>
              <DashboardPage />
            </Layout>
          }
        />
        <Route
          path="/logs"
          element={
            <Layout>
              <LogsPage />
            </Layout>
          }
        />
        <Route
          path="/updates"
          element={
            <Layout>
              <UpdatesPage />
            </Layout>
          }
        />
        <Route
          path="/rules"
          element={
            <Layout>
              <FirewallRulesPage />
            </Layout>
          }
        />
        <Route
          path="/reports"
          element={
            <Layout>
              <ReportsPage />
            </Layout>
          }
        />
        <Route
          path="/settings"
          element={
            <Layout>
              <SettingsPage />
            </Layout>
          }
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
