// src/Layout.jsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Layout({ children }) {
  // Dropdown açık/kapalı kontrol
  const [openUserMenu, setOpenUserMenu] = useState(false);

  const handleLogout = () => {
    // localStorage.removeItem("token");
    alert("Çıkış yapıldı!");
    // window.location.href = "/login";
  };

  return (
    <div className="d-flex flex-column" style={{ minHeight: '100vh' }}>
      {/* ÜST NAVBAR */}
      <nav className="navbar" style={navBarStyle}>
        <div className="container-fluid d-flex align-items-center justify-content-between h-100">

          {/* LOGO + Sol Bilgiler */}
          <div style={{ display: 'flex', flexDirection: 'column' }}>
            <span style={navTitleStyle}>NetGate</span>
            <span style={{ color: '#f0f0f0', fontSize: '0.85rem', marginTop: '-3px' }}>
              Hoş Geldiniz, Sistem Yöneticisi
            </span>
          </div>

          {/* Sağ Taraf: Bazı ikonlar + Kullanıcı Menüsü */}
          <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
            {/* Örnek ikon: Bildirim */}
            <div style={iconWrapperStyle}>
              <i className="bi bi-bell-fill" style={{ fontSize: '1.2rem', color: '#fff' }}></i>
            </div>

            {/* Kullanıcı menü container */}
            <div style={{ position: 'relative' }}>
              <div
                onClick={() => setOpenUserMenu(!openUserMenu)}
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  cursor: 'pointer',
                  color: '#fff'
                }}
              >
                <img
                  src="/assets/user.png"
                  alt="User Avatar"
                  style={{
                    width: '30px',
                    height: '30px',
                    borderRadius: '50%',
                    marginRight: '8px'
                  }}
                />
                <span>admin</span>
                <i className="bi bi-caret-down-fill" style={{ fontSize: '0.8rem', marginLeft: '4px' }}></i>
              </div>

              {/* Dropdown İçeriği */}
              {openUserMenu && (
                <div style={dropdownMenuStyle}>
                  <Link to="#" className="dropdown-item" style={dropdownItemStyle}>
                    Profil
                  </Link>
                  <button
                    className="dropdown-item"
                    style={dropdownItemStyle}
                    onClick={handleLogout}
                  >
                    Çıkış
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </nav>

      {/* ALT KISIM + Yan Menü + İçerik */}
      <div className="flex-grow-1">
        <div className="row g-0 h-100">
          {/* Yan Menü */}
          <div className="col-auto" style={sideMenuStyle}>
            <div className="p-3">
              <h6 className="mb-3 text-uppercase text-secondary">Menü</h6>
              <ul className="nav flex-column">
                <li className="nav-item mb-1">
                  <Link className="nav-link" to="/">Ana Sayfa</Link>
                </li>
                <li className="nav-item mb-1">
                  <Link className="nav-link" to="/logs">Log Yönetimi</Link>
                </li>
                <li className="nav-item mb-1">
                  <Link className="nav-link" to="/updates">Güncellemeler</Link>
                </li>
                <li className="nav-item mb-1">
                  <Link className="nav-link" to="/rules">Güvenlik Kuralları</Link>
                </li>
                <li className="nav-item mb-1">
                  <Link className="nav-link" to="/reports">Raporlar</Link>
                </li>
                <li className="nav-item mb-1">
                  <Link className="nav-link" to="/settings">Sistem Ayarları</Link>
                </li>
              </ul>
            </div>
          </div>

          {/* İçerik */}
          <div className="col p-4" style={{ backgroundColor: '#f8f9fc' }}>
            {children}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Layout;

/* STYLES */
const navBarStyle = {
  height: '60px',
  backgroundColor: '#22863c', // yeşil ya da Forti-benzeri rengi seçebilirsiniz
  padding: '0 1rem'
};
const navTitleStyle = {
  color: '#fff',
  fontSize: '1.4rem',
  fontWeight: 'bold',
  marginBottom: '0'
};
const sideMenuStyle = {
  width: '220px',
  minHeight: 'calc(100vh - 60px)',
  backgroundColor: '#e9ecef'
};
const iconWrapperStyle = {
  cursor: 'pointer'
};
const dropdownMenuStyle = {
  position: 'absolute',
  top: '40px',
  right: '0',
  backgroundColor: '#fff',
  border: '1px solid #ccc',
  borderRadius: '4px',
  minWidth: '120px',
  boxShadow: '0 2px 8px rgba(0,0,0,0.15)'
};
const dropdownItemStyle = {
  display: 'block',
  width: '100%',
  padding: '8px 12px',
  color: '#333',
  textDecoration: 'none',
  textAlign: 'left',
  background: 'none',
  border: 'none',
  cursor: 'pointer'
};
