// src/pages/FirewallWizardPage.jsx
import React, { useState } from "react";
import axios from "axios";

function FirewallWizardPage() {
  const [scenario, setScenario] = useState("rdp");
  const [resultMessage, setResultMessage] = useState("");

  const handleWizard = async (e) => {
    e.preventDefault();

    try {
      const token = localStorage.getItem("token");
      // DİKKAT: ?scenario=rdp
      const url = `http://127.0.0.1:8000/firewall/wizard?scenario=${scenario}`;
      const res = await axios.post(url, null, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setResultMessage(res.data.message);
    } catch (err) {
      setResultMessage("Hata: " + (err.response?.data?.detail || err.message));
    }
  };

  return (
    <div>
      <h2 className="dashboard-title">Kural Sihirbazı</h2>
      <p>Hazır senaryolardan birini seçerek tek tıkla kural ekleyebilirsiniz.</p>

      <div className="card">
        <div className="card-header">Kural Sihirbazı</div>
        <div className="card-body">
          <form onSubmit={handleWizard}>
            <div className="mb-3">
              <label>Senaryo Seç</label>
              <select
                className="form-select"
                value={scenario}
                onChange={(e) => setScenario(e.target.value)}
              >
                <option value="rdp">RDP (3389/TCP inbound)</option>
                <option value="ssh">SSH (22/TCP inbound)</option>
                <option value="webserver">HTTP (80/TCP inbound)</option>
              </select>
            </div>
            <button type="submit" className="btn btn-primary">
              Kuralı Oluştur
            </button>
          </form>
        </div>
      </div>

      {resultMessage && (
        <div className="alert alert-info mt-3">{resultMessage}</div>
      )}
    </div>
  );
}

export default FirewallWizardPage;
