import React, { useEffect, useState } from 'react';
import axios from 'axios';

function DashboardPage() {
  const [cpuPercent, setCpuPercent] = useState(0);
  const [memPercent, setMemPercent] = useState(0);
  const [uptime, setUptime] = useState(0);

  const fetchStatus = async () => {
    try {
      const token = localStorage.getItem("token");
      const resp = await axios.get("/api/status/dashboard", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCpuPercent(resp.data.cpu_percent);
      setMemPercent(resp.data.memory_percent);
      setUptime(resp.data.uptime_seconds);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchStatus();
  }, []);

  return (
    <div style={{ margin: 20 }}>
      <h2>Dashboard</h2>
      <p>CPU Kullan覺m覺: {cpuPercent}%</p>
      <p>Bellek Kullan覺m覺: {memPercent}%</p>
      <p>Sistem Uptime (sn): {uptime}</p>
    </div>
  );
}

export default DashboardPage;
