// File: src/pages/FirewallRulesPage.jsx

import React, { useEffect, useState } from "react";
import axios from "axios";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";

function FirewallRulesPage() {
  const [rules, setRules] = useState([]);
  const [showForm, setShowForm] = useState(false);

  // form
  const [ruleId, setRuleId] = useState(null);
  const [ruleName, setRuleName] = useState("");
  const [sourceIps, setSourceIps] = useState("");
  const [port, setPort] = useState("");
  const [protocol, setProtocol] = useState("TCP");
  const [action, setAction] = useState("ALLOW");
  const [direction, setDirection] = useState("IN");
  const [profile, setProfile] = useState("Any");
  const [description, setDescription] = useState("");
  const [enabled, setEnabled] = useState(true);
  const [priority, setPriority] = useState(100);
  const [scheduleStart, setScheduleStart] = useState("");
  const [scheduleEnd, setScheduleEnd] = useState("");
  const [daysOfWeek, setDaysOfWeek] = useState([]);
  const [groupId, setGroupId] = useState("");

  const isEditMode = ruleId !== null;

  useEffect(() => {
    fetchRules();
  }, []);

  const fetchRules = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://127.0.0.1:8000/firewall/rules", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setRules(res.data);
    } catch (err) {
      alert("Kural listesi alınamadı: " + err.message);
    }
  };

  const handleNewRule = () => {
    clearForm();
    setShowForm(true);
  };

  const clearForm = () => {
    setRuleId(null);
    setRuleName("");
    setSourceIps("");
    setPort("");
    setProtocol("TCP");
    setAction("ALLOW");
    setDirection("IN");
    setProfile("Any");
    setDescription("");
    setEnabled(true);
    setPriority(100);
    setScheduleStart("");
    setScheduleEnd("");
    setDaysOfWeek([]);
    setGroupId("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");
    const ipsArr = sourceIps
      .split(",")
      .map((ip) => ip.trim())
      .filter((ip) => ip);

    const body = {
      rule_name: ruleName,
      source_ips: ipsArr,
      port: port ? Number(port) : null,
      protocol,
      action,
      direction,
      profile,
      description,
      enabled,
      priority: Number(priority),
      schedule_start: scheduleStart || null,
      schedule_end: scheduleEnd || null,
      days_of_week: daysOfWeek.map(d => parseInt(d, 10)),
      group_id: groupId || null
    };

    try {
      let res;
      if (!isEditMode) {
        res = await axios.post("http://127.0.0.1:8000/firewall/rules", body, {
          headers: { Authorization: `Bearer ${token}` }
        });
        alert(res.data.message);
      } else {
        const url = `http://127.0.0.1:8000/firewall/rules/${ruleId}`;
        res = await axios.put(url, body, {
          headers: { Authorization: `Bearer ${token}` }
        });
        alert(res.data.message);
      }
      setShowForm(false);
      fetchRules();
    } catch (err) {
      alert("Kural kaydedilemedi: " + (err.response?.data?.detail || err.message));
    }
  };

  const handleEdit = (rule) => {
    setRuleId(rule._id);
    setRuleName(rule.rule_name);
    setSourceIps(rule.source_ips?.join(",") || "");
    setPort(rule.port || "");
    setProtocol(rule.protocol || "TCP");
    setAction(rule.action || "ALLOW");
    setDirection(rule.direction || "IN");
    setProfile(rule.profile || "Any");
    setDescription(rule.description || "");
    setEnabled(rule.enabled !== false);
    setPriority(rule.priority || 100);
    setScheduleStart(rule.schedule_start || "");
    setScheduleEnd(rule.schedule_end || "");
    setDaysOfWeek(rule.days_of_week || []);
    setGroupId(rule.group_id || "");
    setShowForm(true);
  };

  const handleDelete = async (rule) => {
    if (!window.confirm(`'${rule.rule_name}' kuralını silmek istiyor musunuz?`)) return;
    try {
      const token = localStorage.getItem("token");
      const url = `http://127.0.0.1:8000/firewall/rules/${rule._id}`;
      const res = await axios.delete(url, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert(res.data.message);
      fetchRules();
    } catch (err) {
      alert("Silme hatası: " + (err.response?.data?.detail || err.message));
    }
  };

  const handleDayCheck = (idx) => {
    if (daysOfWeek.includes(idx)) {
      setDaysOfWeek(daysOfWeek.filter((d) => d !== idx));
    } else {
      setDaysOfWeek([...daysOfWeek, idx]);
    }
  };

  // Drag & Drop
  const onDragEnd = (result) => {
    if (!result.destination) return;
    const { source, destination } = result;
    const arr = Array.from(rules);
    const [removed] = arr.splice(source.index, 1);
    arr.splice(destination.index, 0, removed);
    setRules(arr);
    // isterseniz sunucuya priority update...
  };

  return (
    <div>
      <h2 className="dashboard-title">Firewall Kuralları</h2>
      <button className="btn btn-primary mb-3" onClick={handleNewRule}>
        Yeni Kural Ekle
      </button>

      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="rules-list">
          {(provided) => (
            <table
              className="table"
              ref={provided.innerRef}
              {...provided.droppableProps}
            >
              <thead>
                <tr>
                  <th>#</th>
                  <th>Kural Adı</th>
                  <th>Action</th>
                  <th>Port</th>
                  <th>Enabled?</th>
                  <th>İşlem</th>
                </tr>
              </thead>
              <tbody>
                {rules.map((r, index) => (
                  <Draggable key={r._id} draggableId={r._id} index={index}>
                    {(prov) => (
                      <tr
                        ref={prov.innerRef}
                        {...prov.draggableProps}
                        {...prov.dragHandleProps}
                      >
                        <td>{index + 1}</td>
                        <td>{r.rule_name}</td>
                        <td>{r.action}</td>
                        <td>{r.port || "-"}</td>
                        <td>{r.enabled ? "Evet" : "Hayır"}</td>
                        <td>
                          <button
                            className="btn btn-sm btn-success me-2"
                            onClick={() => handleEdit(r)}
                          >
                            Düzenle
                          </button>
                          <button
                            className="btn btn-sm btn-danger"
                            onClick={() => handleDelete(r)}
                          >
                            Sil
                          </button>
                        </td>
                      </tr>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </tbody>
            </table>
          )}
        </Droppable>
      </DragDropContext>

      {showForm && (
        <div className="card mt-3">
          <div className="card-header">
            {isEditMode ? "Kuralı Düzenle" : "Yeni Kural Ekle"}
          </div>
          <div className="card-body">
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label>Kural Adı</label>
                <input
                  className="form-control"
                  required
                  value={ruleName}
                  onChange={(e) => setRuleName(e.target.value)}
                />
              </div>

              <div className="mb-3">
                <label>Kaynak IP(ler) (virgülle ayır)</label>
                <input
                  className="form-control"
                  value={sourceIps}
                  onChange={(e) => setSourceIps(e.target.value)}
                />
              </div>

              <div className="mb-3">
                <label>Port</label>
                <input
                  type="number"
                  className="form-control"
                  value={port}
                  onChange={(e) => setPort(e.target.value)}
                />
              </div>

              <div className="mb-3">
                <label>Protocol</label>
                <select
                  className="form-select"
                  value={protocol}
                  onChange={(e) => setProtocol(e.target.value)}
                >
                  <option value="TCP">TCP</option>
                  <option value="UDP">UDP</option>
                </select>
              </div>

              <div className="mb-3">
                <label>Action</label>
                <select
                  className="form-select"
                  value={action}
                  onChange={(e) => setAction(e.target.value)}
                >
                  <option value="ALLOW">ALLOW</option>
                  <option value="DENY">DENY</option>
                </select>
              </div>

              <div className="mb-3">
                <label>Direction</label>
                <select
                  className="form-select"
                  value={direction}
                  onChange={(e) => setDirection(e.target.value)}
                >
                  <option value="IN">IN</option>
                  <option value="OUT">OUT</option>
                </select>
              </div>

              <div className="mb-3">
                <label>Profile</label>
                <select
                  className="form-select"
                  value={profile}
                  onChange={(e) => setProfile(e.target.value)}
                >
                  <option value="Any">Any</option>
                  <option value="Domain">Domain</option>
                  <option value="Private">Private</option>
                  <option value="Public">Public</option>
                </select>
              </div>

              <div className="mb-3">
                <label>Açıklama</label>
                <input
                  className="form-control"
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                />
              </div>

              <div className="mb-3 form-check">
                <input
                  type="checkbox"
                  className="form-check-input"
                  id="chkEnabled"
                  checked={enabled}
                  onChange={(e) => setEnabled(e.target.checked)}
                />
                <label className="form-check-label" htmlFor="chkEnabled">
                  Kural Etkin
                </label>
              </div>

              <div className="mb-3">
                <label>Öncelik</label>
                <input
                  type="number"
                  className="form-control"
                  value={priority}
                  onChange={(e) => setPriority(e.target.value)}
                />
              </div>

              <div className="mb-3">
                <label>Zaman Başlangıcı (HH:MM)</label>
                <input
                  type="time"
                  className="form-control"
                  value={scheduleStart}
                  onChange={(e) => setScheduleStart(e.target.value)}
                />
              </div>

              <div className="mb-3">
                <label>Zaman Sonu (HH:MM)</label>
                <input
                  type="time"
                  className="form-control"
                  value={scheduleEnd}
                  onChange={(e) => setScheduleEnd(e.target.value)}
                />
              </div>

              <div className="mb-3">
                <label>Haftanın Günleri</label>
                <div>
                  {["Pzt","Sal","Çar","Per","Cum","Cmt","Paz"].map((label, idx) => (
                    <div key={idx} className="form-check form-check-inline">
                      <input
                        type="checkbox"
                        className="form-check-input"
                        checked={daysOfWeek.includes(idx)}
                        onChange={() => handleDayCheck(idx)}
                        id={`chkDay${idx}`}
                      />
                      <label className="form-check-label" htmlFor={`chkDay${idx}`}>
                        {label}
                      </label>
                    </div>
                  ))}
                </div>
              </div>

              <div className="mb-3">
                <label>Group ID (opsiyonel)</label>
                <input
                  className="form-control"
                  value={groupId}
                  onChange={(e) => setGroupId(e.target.value)}
                />
              </div>

              <button type="submit" className="btn btn-primary">
                {isEditMode ? "Güncelle" : "Kaydet"}
              </button>
              <button
                type="button"
                className="btn btn-secondary ms-2"
                onClick={() => setShowForm(false)}
              >
                Vazgeç
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default FirewallRulesPage;
