import React, { useEffect, useState } from 'react';
import axios from 'axios';

function DashboardPage() {
  const [cpuPercent, setCpuPercent] = useState(0);
  const [memPercent, setMemPercent] = useState(0);
  const [uptime, setUptime] = useState(0);

  const fetchStatus = async () => {
    try {
      const token = localStorage.getItem("token");
      const resp = await axios.get("/api/status/dashboard", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCpuPercent(resp.data.cpu_percent);
      setMemPercent(resp.data.memory_percent);
      setUptime(resp.data.uptime_seconds);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchStatus();
  }, []);

  return (
    <div className="row">
      <h2 className="mb-4">Dashboard</h2>

      <div className="col-md-4">
        <div className="card text-white bg-primary mb-3">
          <div className="card-header">CPU Kullanımı</div>
          <div className="card-body">
            <h5 className="card-title">{cpuPercent}%</h5>
            <p className="card-text">Sistemin anlık CPU kullanım oranı.</p>
          </div>
        </div>
      </div>

      <div className="col-md-4">
        <div className="card text-white bg-success mb-3">
          <div className="card-header">Bellek Kullanımı</div>
          <div className="card-body">
            <h5 className="card-title">{memPercent}%</h5>
            <p className="card-text">Aktif bellek kullanım yüzdesi.</p>
          </div>
        </div>
      </div>

      <div className="col-md-4">
        <div className="card text-white bg-info mb-3">
          <div className="card-header">Sistem Uptime</div>
          <div className="card-body">
            <h5 className="card-title">{uptime} sn</h5>
            <p className="card-text">Sistem açık kalma süresi (saniye).</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default DashboardPage;
