// src/Layout.jsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

// Material UI
import {
  AppBar,
  Box,
  CssBaseline,
  Drawer,
  Toolbar,
  Typography,
  IconButton,
  List,
  ListItemButton,
  ListItemText
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';

// Drawer sabit genişlik
const drawerWidth = 220;

function Layout({ children }) {
  const [mobileOpen, setMobileOpen] = useState(false);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  // Menü ögeleri
  const menuItems = [
    { label: 'Ana Sayfa', path: '/' },
    { label: 'Log Yönetimi', path: '/logs' },
    { label: 'Güncellemeler', path: '/updates' },
    { label: 'Güvenlik Kuralları', path: '/rules' },
    { label: 'Raporlar', path: '/reports' },
    { label: 'Sistem Ayarları', path: '/settings' }
  ];

  // Drawer içeriği
  const drawer = (
    <div>
      <Toolbar sx={{ minHeight: 80 }} />
      <List>
        {menuItems.map((item) => (
          <ListItemButton
            key={item.path}
            component={Link}
            to={item.path}
            sx={{ textTransform: 'none' }}
          >
            <ListItemText primary={item.label} />
          </ListItemButton>
        ))}
      </List>
    </div>
  );

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />

      {/* Üst AppBar */}
      <AppBar
        position="fixed"
        sx={{
          width: { md: `calc(100% - ${drawerWidth}px)` },
          ml: { md: `${drawerWidth}px` }
        }}
      >
        <Toolbar>
          {/* Mobilde menu butonu */}
          <IconButton
            color="inherit"
            edge="start"
            onClick={handleDrawerToggle}
            sx={{ mr: 2, display: { md: 'none' } }}
          >
            <MenuIcon />
          </IconButton>

          <Box sx={{ display: 'flex', flexDirection: 'column' }}>
            <Typography variant="h6" component="div">
              NetGate
            </Typography>
            <Typography variant="caption" component="div">
              Hoş Geldiniz, Sistem Yöneticisi
            </Typography>
          </Box>

          {/* Profil / Bildirim butonları vs. için yer */}
        </Toolbar>
      </AppBar>

      {/* Sol Drawer */}
      <Box
        component="nav"
        sx={{ width: { md: drawerWidth }, flexShrink: { md: 0 } }}
        aria-label="menu"
      >
        {/* Mobilde geçişli Drawer */}
        <Drawer
          variant="temporary"
          open={mobileOpen}
          onClose={handleDrawerToggle}
          ModalProps={{
            keepMounted: true, // Mobilde performans
          }}
          sx={{
            display: { xs: 'block', md: 'none' },
            '& .MuiDrawer-paper': { width: drawerWidth }
          }}
        >
          {drawer}
        </Drawer>

        {/* Masaüstünde sabit Drawer */}
        <Drawer
          variant="permanent"
          sx={{
            display: { xs: 'none', md: 'block' },
            '& .MuiDrawer-paper': { width: drawerWidth, boxSizing: 'border-box' }
          }}
          open
        >
          {drawer}
        </Drawer>
      </Box>

      {/* Asıl içerik */}
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          width: { md: `calc(100% - ${drawerWidth}px)` },
          minHeight: '100vh'
        }}
      >
        <Toolbar /> {/* AppBar yüksekliği kadar boşluk */}
        {children}
      </Box>
    </Box>
  );
}

export default Layout;
