// src/pages/SettingsPage.jsx
import React, { useState } from 'react';
import {
  Box,
  Typography,
  Card,
  CardHeader,
  CardContent,
  FormControlLabel,
  Checkbox,
  Select,
  MenuItem,
  Button,
  FormControl,
  InputLabel
} from '@mui/material';

function SettingsPage() {
  const [timezone, setTimezone] = useState("Europe/Istanbul");
  const [autoUpdate, setAutoUpdate] = useState(true);
  const [backupSchedule, setBackupSchedule] = useState("Haftalık");

  const handleSave = () => {
    alert(
      `Kaydedildi!
Zaman Dilimi: ${timezone}
Otomatik Güncelleme: ${autoUpdate}
Yedekleme Planı: ${backupSchedule}`
    );
  };

  return (
    <Box>
      <Typography variant="h5" gutterBottom>
        Sistem Ayarları
      </Typography>

      <Card sx={{ mb: 3 }}>
        <CardHeader title="Genel Ayarlar" />
        <CardContent>
          <Box mb={2}>
            <Typography>Zaman Dilimi</Typography>
            <FormControl fullWidth>
              <InputLabel>Zaman Dilimi</InputLabel>
              <Select
                value={timezone}
                label="Zaman Dilimi"
                onChange={(e) => setTimezone(e.target.value)}
              >
                <MenuItem value="Europe/Istanbul">Europe/Istanbul</MenuItem>
                <MenuItem value="UTC">UTC</MenuItem>
                <MenuItem value="Europe/Berlin">Europe/Berlin</MenuItem>
                <MenuItem value="America/New_York">America/New_York</MenuItem>
              </Select>
            </FormControl>
          </Box>

          <Box mb={2}>
            <FormControlLabel
              control={
                <Checkbox
                  checked={autoUpdate}
                  onChange={() => setAutoUpdate(!autoUpdate)}
                />
              }
              label="Otomatik Güncellemeleri Etkinleştir"
            />
          </Box>
        </CardContent>
      </Card>

      <Card sx={{ mb: 3 }}>
        <CardHeader title="Yedekleme Planı" />
        <CardContent>
          <Box mb={2}>
            <Typography>Yedekleme Sıklığı</Typography>
            <FormControl fullWidth>
              <InputLabel>Sıklık</InputLabel>
              <Select
                value={backupSchedule}
                label="Sıklık"
                onChange={(e) => setBackupSchedule(e.target.value)}
              >
                <MenuItem value="Günlük">Günlük</MenuItem>
                <MenuItem value="Haftalık">Haftalık</MenuItem>
                <MenuItem value="Aylık">Aylık</MenuItem>
              </Select>
            </FormControl>
          </Box>
          <Button variant="contained" onClick={handleSave}>
            Kaydet
          </Button>
        </CardContent>
      </Card>
    </Box>
  );
}

export default SettingsPage;
