// src/App.js
import React from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Layout from './Layout';

import DashboardPage from './pages/DashboardPage';
import LogsPage from './pages/LogsPage';
import FirewallRulesPage from './pages/FirewallRulesPage';
import UpdatesPage from './pages/UpdatesPage';
import ReportsPage from './pages/ReportsPage';
import SettingsPage from './pages/SettingsPage';
import LoginPage from './pages/LoginPage';  // <-- Yeni

function App() {
  return (
    <BrowserRouter>
      <Routes>
        {/* Sadece Login */}
        <Route path="/login" element={<LoginPage />} />

        {/* Login sonrasında Layout'lu kısım */}
        <Route path="/" element={<Layout><DashboardPage /></Layout>} />
        <Route path="/logs" element={<Layout><LogsPage /></Layout>} />
        <Route path="/updates" element={<Layout><UpdatesPage /></Layout>} />
        <Route path="/rules" element={<Layout><FirewallRulesPage /></Layout>} />
        <Route path="/reports" element={<Layout><ReportsPage /></Layout>} />
        <Route path="/settings" element={<Layout><SettingsPage /></Layout>} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
