// File: src/pages/FirewallRulesPage.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";

function FirewallRulesPage() {
  const [rules, setRules] = useState([]);

  // Form state
  const [ruleId, setRuleId] = useState(null); // DB'deki _id (edit için)
  const [ruleName, setRuleName] = useState("");
  const [sourceIp, setSourceIp] = useState("");
  const [port, setPort] = useState("");
  const [protocol, setProtocol] = useState("TCP");
  const [action, setAction] = useState("ALLOW");
  const [direction, setDirection] = useState("IN");
  const [profile, setProfile] = useState("Any");
  const [description, setDescription] = useState("");

  // Edit mode flag
  const isEditMode = ruleId !== null;

  useEffect(() => {
    fetchRules();
  }, []);

  const fetchRules = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://127.0.0.1:8000/firewall/rules", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setRules(res.data);
    } catch (err) {
      console.error(err);
      alert("Kurallar çekilirken hata: " + (err.response?.data?.detail || err.message));
    }
  };

  const handleEditClick = (rule) => {
    // Tıklanan kuralın verilerini forma yükleyelim
    setRuleId(rule._id);
    setRuleName(rule.rule_name);
    setSourceIp(rule.source_ip || "");
    setPort(rule.port || "");
    setProtocol(rule.protocol || "TCP");
    setAction(rule.action || "ALLOW");
    setDirection(rule.direction || "IN");
    setProfile(rule.profile || "Any");
    setDescription(rule.description || "");
  };

  const handleDelete = async (rule_id) => {
    if (!window.confirm("Bu kuralı silmek istediğinize emin misiniz?")) return;
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://127.0.0.1:8000/firewall/rules/${rule_id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("Kural silindi");
      fetchRules();
    } catch (err) {
      console.error(err);
      alert("Kural silinirken hata: " + (err.response?.data?.detail || err.message));
    }
  };

  const clearForm = () => {
    setRuleId(null);
    setRuleName("");
    setSourceIp("");
    setPort("");
    setProtocol("TCP");
    setAction("ALLOW");
    setDirection("IN");
    setProfile("Any");
    setDescription("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    const body = {
      rule_name: ruleName,
      source_ip: sourceIp || null,
      port: port ? Number(port) : null,
      protocol,
      action,
      direction,
      profile,
      description: description || null,
    };

    try {
      let res;
      if (!isEditMode) {
        // CREATE (POST)
        res = await axios.post("http://127.0.0.1:8000/firewall/rules", body, {
          headers: { Authorization: `Bearer ${token}` },
        });
        alert(res.data.message || "Kural eklendi");
      } else {
        // UPDATE (PUT)
        res = await axios.put(`http://127.0.0.1:8000/firewall/rules/${ruleId}`, body, {
          headers: { Authorization: `Bearer ${token}` },
        });
        alert(res.data.message || "Kural güncellendi");
      }
      clearForm();
      fetchRules();
    } catch (err) {
      console.error(err);
      alert("Hata oluştu: " + (err.response?.data?.detail || err.message));
    }
  };

  const handleCancelEdit = () => {
    clearForm();
  };

  return (
    <div>
      <h2 className="dashboard-title">Firewall Kuralları</h2>

      {/* Kural listesi */}
      <div className="card mb-4">
        <div className="card-header">Kural Listesi</div>
        <div className="card-body p-0">
          <table className="table mb-0">
            <thead>
              <tr>
                <th>Kural Adı</th>
                <th>Kaynak IP</th>
                <th>Port</th>
                <th>Protocol</th>
                <th>Action</th>
                <th>Direction</th>
                <th>Profile</th>
                <th>Açıklama</th>
                <th>İşlem</th>
              </tr>
            </thead>
            <tbody>
              {rules.map((r) => (
                <tr key={r._id}>
                  <td>{r.rule_name}</td>
                  <td>{r.source_ip || "-"}</td>
                  <td>{r.port || "-"}</td>
                  <td>{r.protocol || "-"}</td>
                  <td>{r.action}</td>
                  <td>{r.direction}</td>
                  <td>{r.profile}</td>
                  <td>{r.description || "-"}</td>
                  <td>
                    <button
                      className="btn btn-sm btn-primary me-2"
                      onClick={() => handleEditClick(r)}
                    >
                      Düzenle
                    </button>
                    <button
                      className="btn btn-sm btn-danger"
                      onClick={() => handleDelete(r._id)}
                    >
                      Sil
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Form */}
      <div className="card">
        <div className="card-header">
          {isEditMode ? "Kural Düzenle" : "Yeni Kural Ekle"}
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label>Kural Adı</label>
              <input
                className="form-control"
                value={ruleName}
                onChange={(e) => setRuleName(e.target.value)}
                required
              />
            </div>

            <div className="mb-3">
              <label>Kaynak IP (opsiyonel)</label>
              <input
                className="form-control"
                value={sourceIp}
                onChange={(e) => setSourceIp(e.target.value)}
                placeholder="örn: 192.168.1.50 veya 10.0.0.0/24"
              />
            </div>

            <div className="mb-3">
              <label>Port (opsiyonel)</label>
              <input
                type="number"
                className="form-control"
                value={port}
                onChange={(e) => setPort(e.target.value)}
              />
            </div>

            <div className="mb-3">
              <label>Protocol</label>
              <select
                className="form-select"
                value={protocol}
                onChange={(e) => setProtocol(e.target.value)}
              >
                <option value="TCP">TCP</option>
                <option value="UDP">UDP</option>
              </select>
            </div>

            <div className="mb-3">
              <label>Action</label>
              <select
                className="form-select"
                value={action}
                onChange={(e) => setAction(e.target.value)}
              >
                <option value="ALLOW">ALLOW</option>
                <option value="DENY">DENY</option>
              </select>
            </div>

            <div className="mb-3">
              <label>Direction</label>
              <select
                className="form-select"
                value={direction}
                onChange={(e) => setDirection(e.target.value)}
              >
                <option value="IN">IN (Inbound)</option>
                <option value="OUT">OUT (Outbound)</option>
              </select>
            </div>

            <div className="mb-3">
              <label>Profile</label>
              <select
                className="form-select"
                value={profile}
                onChange={(e) => setProfile(e.target.value)}
              >
                <option value="Any">Any</option>
                <option value="Domain">Domain</option>
                <option value="Private">Private</option>
                <option value="Public">Public</option>
              </select>
            </div>

            <div className="mb-3">
              <label>Açıklama (opsiyonel)</label>
              <input
                className="form-control"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              />
            </div>

            <button type="submit" className="btn btn-primary me-2">
              {isEditMode ? "Güncelle" : "Ekle"}
            </button>
            {isEditMode && (
              <button type="button" className="btn btn-secondary" onClick={handleCancelEdit}>
                İptal
              </button>
            )}
          </form>
        </div>
      </div>
    </div>
  );
}

export default FirewallRulesPage;
