// src/pages/NetworkInterfacesPage.jsx

import React, { useEffect, useState } from 'react';
import axios from 'axios';

// Basit Regex'ler
const ipRegex = /^((25[0-5]|2[0-4]\d|[01]?\d?\d)\.){3}(25[0-5]|2[0-4]\d|[01]?\d?\d)$/;
const maskRegex = /^((255|254|252|248|240|224|192|128|0)\.){3}(255|254|252|248|240|224|192|128|0)$/;
    // Subnet mask en basit "x.x.x.x" valid, (isteğe göre net check)
const dnsRegex = ipRegex;  // DNS de IP format

function NetworkInterfacesPage() {
  const [interfaces, setInterfaces] = useState([]);

  // "Yeni Ekle" form state
  const [intfName, setIntfName] = useState("Ethernet");
  const [ipMode, setIpMode] = useState("static");
  const [ipAddress, setIpAddress] = useState("");
  const [subnetMask, setSubnetMask] = useState("");
  const [gateway, setGateway] = useState("");
  const [dnsPrimary, setDnsPrimary] = useState("");
  const [dnsSecondary, setDnsSecondary] = useState("");
  const [adminEnabled, setAdminEnabled] = useState(true);
  const [mtu, setMtu] = useState(1500);
  const [vlanId, setVlanId] = useState("");

  // Validation errors (string; "" => no error)
  const [ipErr, setIpErr] = useState("");
  const [maskErr, setMaskErr] = useState("");
  const [dnsPriErr, setDnsPriErr] = useState("");
  const [dnsSecErr, setDnsSecErr] = useState("");
  const [vlanErr, setVlanErr] = useState("");
  const [mtuErr, setMtuErr] = useState("");

  // Edit mode
  const [isEditMode, setIsEditMode] = useState(false);
  // ... Edit states (omitted for brevity if you want a simpler example)...

  useEffect(() => {
    fetchInterfaces();
  }, []);

  const fetchInterfaces = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://127.0.0.1:8000/network/interfaces", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setInterfaces(res.data);
    } catch (err) {
      console.error(err);
      alert("Arayüzleri çekerken hata: " + err.message);
    }
  };

  // Validation handlers
  const handleIpChange = (e) => {
    const val = e.target.value;
    setIpAddress(val);
    if (val && !ipRegex.test(val)) {
      setIpErr("Geçersiz IP formatı");
    } else {
      setIpErr("");
    }
  };

  const handleMaskChange = (e) => {
    const val = e.target.value;
    setSubnetMask(val);
    if (val && !maskRegex.test(val)) {
      setMaskErr("Geçersiz Subnet Mask");
    } else {
      setMaskErr("");
    }
  };

  const handleDnsPrimaryChange = (e) => {
    const val = e.target.value;
    setDnsPrimary(val);
    if (val && !dnsRegex.test(val)) {
      setDnsPriErr("Geçersiz DNS IP");
    } else {
      setDnsPriErr("");
    }
  };

  const handleDnsSecondaryChange = (e) => {
    const val = e.target.value;
    setDnsSecondary(val);
    if (val && !dnsRegex.test(val)) {
      setDnsSecErr("Geçersiz DNS IP");
    } else {
      setDnsSecErr("");
    }
  };

  const handleMtuChange = (e) => {
    const val = e.target.value;
    setMtu(val);
    const num = parseInt(val, 10);
    if (num < 576 || num > 9000) {
      setMtuErr("MTU 576–9000 aralığında olmalı (örnek).");
    } else {
      setMtuErr("");
    }
  };

  const handleVlanChange = (e) => {
    const val = e.target.value;
    setVlanId(val);
    const num = parseInt(val, 10);
    if (num < 0 || num > 4095) {
      setVlanErr("VLAN ID 0–4095 aralığında olmalı.");
    } else {
      setVlanErr("");
    }
  };

  // Submit new
  const handleSave = async (e) => {
    e.preventDefault();
    // Son bir kontrol: static moddaysa IP/mask vs. doldurulmuş mu?
    if (ipMode === "static") {
      if (!ipAddress || ipErr) {
        alert("IP address hatalı veya boş!");
        return;
      }
      if (!subnetMask || maskErr) {
        alert("Subnet Mask hatalı veya boş!");
        return;
      }
      // gateway opsiyonel
      if (dnsPrimary && dnsPriErr) {
        alert("DNS Primary hatalı");
        return;
      }
      if (dnsSecondary && dnsSecErr) {
        alert("DNS Secondary hatalı");
        return;
      }
    }

    if (mtuErr || vlanErr) {
      alert("MTU veya VLAN ID geçersiz!");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const body = {
        interface_name: intfName,
        ip_mode: ipMode,
        ip_address: ipMode === "static" ? ipAddress : null,
        subnet_mask: ipMode === "static" ? subnetMask : null,
        gateway: ipMode === "static" ? gateway : null,
        dns_primary: ipMode === "static" ? (dnsPrimary || null) : null,
        dns_secondary: ipMode === "static" ? (dnsSecondary || null) : null,
        admin_enabled: adminEnabled,
        mtu: mtu ? Number(mtu) : null,
        vlan_id: vlanId ? Number(vlanId) : null,
      };
      const res = await axios.post("http://127.0.0.1:8000/network/interfaces", body, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert(res.data.message || "Arayüz eklendi");
      // Clear form
      setIpMode("static");
      setIpAddress("");
      setSubnetMask("");
      setGateway("");
      setDnsPrimary("");
      setDnsSecondary("");
      setAdminEnabled(true);
      setMtu(1500);
      setVlanId("");
      fetchInterfaces();
    } catch (err) {
      console.error(err);
      // backend parse_netsh_error or generic
      const msg = err.response?.data?.detail || err.message;
      alert("Hata oluştu: " + msg);
    }
  };

  // Basit date format
  const formatDateIstanbul = (iso) => {
    if (!iso) return "-";
    const dt = new Date(iso);
    return dt.toLocaleString("tr-TR", { timeZone: "Europe/Istanbul" });
  };

  return (
    <div>
      <h2 className="dashboard-title">Network Interface Ayarları</h2>

      <div className="card mb-3">
        <div className="card-header">Mevcut Interface Ayarları</div>
        <div className="card-body p-0">
          <table className="table mb-0">
            <thead>
              <tr>
                <th>Arayüz Adı</th>
                <th>Mode</th>
                <th>IP/Subnet</th>
                <th>Gateway</th>
                <th>DNS Primary</th>
                <th>DNS Secondary</th>
                <th>Admin</th>
                <th>Link State</th>
                <th>MTU</th>
                <th>VLAN</th>
                <th>Güncellendi</th>
              </tr>
            </thead>
            <tbody>
              {interfaces.map((inf) => (
                <tr key={inf._id}>
                  <td>{inf.interface_name}</td>
                  <td>{inf.ip_mode}</td>
                  <td>
                    {inf.ip_address || "-"} / {inf.subnet_mask || "-"}
                  </td>
                  <td>{inf.gateway || "-"}</td>
                  <td>{inf.dns_primary || "-"}</td>
                  <td>{inf.dns_secondary || "-"}</td>
                  <td>{inf.admin_enabled ? "Enabled" : "Disabled"}</td>
                  <td>{inf.link_state || "?"}</td>
                  <td>{inf.mtu || "-"}</td>
                  <td>{inf.vlan_id || "-"}</td>
                  <td>{formatDateIstanbul(inf.updated_at)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Yeni ayar formu */}
      <div className="card">
        <div className="card-header">Yeni/Değiştir IP Ayarı</div>
        <div className="card-body">
          <form onSubmit={handleSave}>
            <div className="mb-3">
              <label>Interface Adı</label>
              <input
                className="form-control"
                value={intfName}
                onChange={(e) => setIntfName(e.target.value)}
              />
            </div>

            <div className="mb-3">
              <label>Mode (DHCP/Static)</label>
              <select
                className="form-select"
                value={ipMode}
                onChange={(e) => setIpMode(e.target.value)}
              >
                <option value="static">Statik</option>
                <option value="dhcp">DHCP</option>
              </select>
            </div>

            {/* IP form */}
            {ipMode === "static" && (
              <>
                <div className="mb-3">
                  <label>IP Address</label>
                  <input
                    className="form-control"
                    value={ipAddress}
                    onChange={handleIpChange}
                    placeholder="192.168.1.50"
                  />
                  {ipErr && <small style={{ color: "red" }}>{ipErr}</small>}
                </div>
                <div className="mb-3">
                  <label>Subnet Mask</label>
                  <input
                    className="form-control"
                    value={subnetMask}
                    onChange={handleMaskChange}
                    placeholder="255.255.255.0"
                  />
                  {maskErr && <small style={{ color: "red" }}>{maskErr}</small>}
                </div>
                <div className="mb-3">
                  <label>Gateway (opsiyonel)</label>
                  <input
                    className="form-control"
                    value={gateway}
                    onChange={(e) => setGateway(e.target.value)}
                    placeholder="192.168.1.1"
                  />
                </div>
                <div className="mb-3">
                  <label>DNS Primary (opsiyonel)</label>
                  <input
                    className="form-control"
                    value={dnsPrimary}
                    onChange={handleDnsPrimaryChange}
                    placeholder="8.8.8.8"
                  />
                  {dnsPriErr && <small style={{ color: "red" }}>{dnsPriErr}</small>}
                </div>
                <div className="mb-3">
                  <label>DNS Secondary (opsiyonel)</label>
                  <input
                    className="form-control"
                    value={dnsSecondary}
                    onChange={handleDnsSecondaryChange}
                    placeholder="8.8.4.4"
                  />
                  {dnsSecErr && <small style={{ color: "red" }}>{dnsSecErr}</small>}
                </div>
              </>
            )}

            <div className="mb-3 form-check">
              <input
                type="checkbox"
                className="form-check-input"
                checked={adminEnabled}
                onChange={(e) => setAdminEnabled(e.target.checked)}
                id="chkAdmin"
              />
              <label className="form-check-label" htmlFor="chkAdmin">
                Arayüz Etkin (Up/Down)
              </label>
            </div>

            <div className="mb-3">
              <label>MTU</label>
              <input
                type="number"
                className="form-control"
                value={mtu}
                onChange={handleMtuChange}
              />
              {mtuErr && <small style={{ color: "red" }}>{mtuErr}</small>}
            </div>

            <div className="mb-3">
              <label>VLAN ID (opsiyonel)</label>
              <input
                type="number"
                className="form-control"
                value={vlanId}
                onChange={handleVlanChange}
              />
              {vlanErr && <small style={{ color: "red" }}>{vlanErr}</small>}
            </div>

            <button type="submit" className="btn btn-primary">
              Kaydet
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default NetworkInterfacesPage;
