// src/pages/SettingsPage.jsx
import React, { useState } from 'react';

function SettingsPage() {
  const [timezone, setTimezone] = useState("Europe/Istanbul");
  const [autoUpdate, setAutoUpdate] = useState(true);
  const [backupSchedule, setBackupSchedule] = useState("Haftalık");

  const handleSave = () => {
    alert(
      `Kaydedildi!\nZaman Dilimi: ${timezone}\nOtomatik Güncelleme: ${autoUpdate}\nYedekleme Planı: ${backupSchedule}`
    );
    // Burada API'ye POST PUT isteği atarak ayarları kaydedebilirsiniz.
  };

  return (
    <div>
      <h2 className="mb-4">Sistem Ayarları</h2>

      <div className="card mb-3">
        <div className="card-header">Genel Ayarlar</div>
        <div className="card-body">
          {/* Zaman Dilimi */}
          <div className="mb-3">
            <label className="form-label">Zaman Dilimi</label>
            <select
              className="form-select"
              value={timezone}
              onChange={(e) => setTimezone(e.target.value)}
            >
              <option value="Europe/Istanbul">Europe/Istanbul</option>
              <option value="UTC">UTC</option>
              <option value="Europe/Berlin">Europe/Berlin</option>
              <option value="America/New_York">America/New_York</option>
            </select>
          </div>

          {/* Otomatik Güncelleme */}
          <div className="mb-3 form-check">
            <input
              className="form-check-input"
              type="checkbox"
              id="autoUpdateCheck"
              checked={autoUpdate}
              onChange={() => setAutoUpdate(!autoUpdate)}
            />
            <label className="form-check-label" htmlFor="autoUpdateCheck">
              Otomatik Güncellemeleri Etkinleştir
            </label>
          </div>
        </div>
      </div>

      <div className="card mb-3">
        <div className="card-header">Yedekleme Planı</div>
        <div className="card-body">
          <div className="mb-3">
            <label className="form-label">Yedekleme Sıklığı</label>
            <select
              className="form-select"
              value={backupSchedule}
              onChange={(e) => setBackupSchedule(e.target.value)}
            >
              <option value="Günlük">Günlük</option>
              <option value="Haftalık">Haftalık</option>
              <option value="Aylık">Aylık</option>
            </select>
          </div>

          {/* Kaydet Butonu */}
          <button className="btn btn-primary" onClick={handleSave}>
            Kaydet
          </button>
        </div>
      </div>
    </div>
  );
}

export default SettingsPage;
