// src/pages/FirewallRulesPage.jsx
import React, { useState } from 'react';
import {
  Box,
  Typography,
  TextField,
  Select,
  MenuItem,
  Button,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  TableContainer,
  Card,
  CardHeader,
  CardContent,
  Paper
} from '@mui/material';

function FirewallRulesPage() {
  const [newRuleName, setNewRuleName] = useState("");
  const [newRuleType, setNewRuleType] = useState("IP Yasaklama");
  const [newPriority, setNewPriority] = useState(3);
  const [newDesc, setNewDesc] = useState("");

  // Örnek veri
  const [rules, setRules] = useState([
    { id: 1, name: "SSH Engelleme", type: "Port Kısıtlama", priority: 2, isActive: true },
    { id: 2, name: "IP Karaliste", type: "IP Yasaklama", priority: 1, isActive: false },
  ]);

  const createNewRule = () => {
    const newId = Math.floor(Math.random() * 10000);
    const newRule = {
      id: newId,
      name: newRuleName,
      type: newRuleType,
      priority: newPriority,
      desc: newDesc,
      isActive: true,
    };
    setRules((prev) => [...prev, newRule]);
    // Sıfırla
    setNewRuleName("");
    setNewRuleType("IP Yasaklama");
    setNewPriority(3);
    setNewDesc("");
  };

  return (
    <Box>
      <Typography variant="h5" gutterBottom>
        Güvenlik Kuralları
      </Typography>

      {/* Kural Tablosu */}
      <TableContainer component={Paper} sx={{ mb: 3 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Kural Adı</TableCell>
              <TableCell>Tipi</TableCell>
              <TableCell>Öncelik</TableCell>
              <TableCell>Durum</TableCell>
              <TableCell>İşlem</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {rules.map((rule) => (
              <TableRow key={rule.id}>
                <TableCell>{rule.name}</TableCell>
                <TableCell>{rule.type}</TableCell>
                <TableCell>{rule.priority}</TableCell>
                <TableCell>{rule.isActive ? "Aktif" : "Pasif"}</TableCell>
                <TableCell>
                  <Button variant="outlined" size="small" sx={{ mr: 1 }}>
                    Düzenle
                  </Button>
                  {rule.isActive ? (
                    <Button variant="contained" color="warning" size="small">
                      Pasifleştir
                    </Button>
                  ) : (
                    <Button variant="contained" color="success" size="small">
                      Aktifleştir
                    </Button>
                  )}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      {/* Yeni Kural Ekle */}
      <Card>
        <CardHeader title="Yeni Kural Ekle" />
        <CardContent>
          <Box mb={2}>
            <TextField
              label="Kural Adı"
              variant="outlined"
              fullWidth
              value={newRuleName}
              onChange={(e) => setNewRuleName(e.target.value)}
            />
          </Box>
          <Box mb={2}>
            <Typography>Tipi:</Typography>
            <Select
              value={newRuleType}
              onChange={(e) => setNewRuleType(e.target.value)}
              fullWidth
            >
              <MenuItem value="IP Yasaklama">IP Yasaklama</MenuItem>
              <MenuItem value="Port Kısıtlama">Port Kısıtlama</MenuItem>
              <MenuItem value="Protokol Engelleme">Protokol Engelleme</MenuItem>
            </Select>
          </Box>
          <Box mb={2}>
            <TextField
              label="Öncelik"
              type="number"
              variant="outlined"
              fullWidth
              value={newPriority}
              onChange={(e) => setNewPriority(e.target.value)}
            />
          </Box>
          <Box mb={2}>
            <TextField
              label="Açıklama"
              multiline
              rows={2}
              variant="outlined"
              fullWidth
              value={newDesc}
              onChange={(e) => setNewDesc(e.target.value)}
            />
          </Box>
          <Button variant="contained" onClick={createNewRule}>
            Kaydet
          </Button>
        </CardContent>
      </Card>
    </Box>
  );
}

export default FirewallRulesPage;
