// src/pages/NetworkInterfacesPage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function NetworkInterfacesPage() {
  // Liste
  const [interfaces, setInterfaces] = useState([]);

  // "Yeni Ekle" form state
  const [intfName, setIntfName] = useState("Ethernet");
  const [ipAddress, setIpAddress] = useState("");
  const [subnetMask, setSubnetMask] = useState("");
  const [gateway, setGateway] = useState("");
  const [dnsPrimary, setDnsPrimary] = useState("");
  const [dnsSecondary, setDnsSecondary] = useState("");

  // "Edit Mode" state
  const [isEditMode, setIsEditMode] = useState(false);
  // Bilgileri
  const [editInterfaceName, setEditInterfaceName] = useState("");
  const [editIp, setEditIp] = useState("");
  const [editMask, setEditMask] = useState("");
  const [editGateway, setEditGateway] = useState("");
  const [editDnsPrimary, setEditDnsPrimary] = useState("");
  const [editDnsSecondary, setEditDnsSecondary] = useState("");

  useEffect(() => {
    fetchInterfaces();
  }, []);

  const fetchInterfaces = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get("http://127.0.0.1:8000/network/interfaces", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setInterfaces(res.data);
    } catch (err) {
      console.error(err);
      alert("Arayüzleri çekerken hata: " + err.message);
    }
  };

  // 1) Yeni ekleme (POST)
  const handleSave = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const body = {
        interface_name: intfName,
        ip_address: ipAddress,
        subnet_mask: subnetMask,
        gateway: gateway || null,
        dns_primary: dnsPrimary || null,
        dns_secondary: dnsSecondary || null
      };

      const res = await axios.post(
        "http://127.0.0.1:8000/network/interfaces",
        body,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      alert(res.data.message || "Ayar kaydedildi");
      // formu sıfırla
      setIpAddress("");
      setSubnetMask("");
      setGateway("");
      setDnsPrimary("");
      setDnsSecondary("");
      // liste yenile
      fetchInterfaces();
    } catch (err) {
      console.error(err);
      alert("Ayar kaydedilirken hata: " + (err.response?.data?.detail || err.message));
    }
  };

  // 2) "Düzenle" butonuna basınca
  const handleEditClick = (inf) => {
    setIsEditMode(true);
    setEditInterfaceName(inf.interface_name);
    setEditIp(inf.ip_address);
    setEditMask(inf.subnet_mask);
    setEditGateway(inf.gateway || "");
    setEditDnsPrimary(inf.dns_primary || "");
    setEditDnsSecondary(inf.dns_secondary || "");
  };

  // 3) "Güncelle" butonu (PUT)
  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const body = {
        interface_name: editInterfaceName,
        ip_address: editIp,
        subnet_mask: editMask,
        gateway: editGateway || null,
        dns_primary: editDnsPrimary || null,
        dns_secondary: editDnsSecondary || null
      };
      // PUT /network/interfaces/{iface_name}
      const url = `http://127.0.0.1:8000/network/interfaces/${editInterfaceName}`;
      const res = await axios.put(url, body, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      alert(res.data.message || "Güncelleme başarılı");
      setIsEditMode(false);
      fetchInterfaces();
    } catch (err) {
      console.error(err);
      alert("Güncelleme hatası: " + (err.response?.data?.detail || err.message));
    }
  };

  const handleCancelEdit = () => {
    setIsEditMode(false);
  };

  return (
    <div>
      <h2 className="dashboard-title">Network Interface Ayarları</h2>

      {/* Tablo */}
      <div className="card mb-3">
        <div className="card-header">Mevcut Interface Ayarları</div>
        <div className="card-body p-0">
          <table className="table mb-0">
            <thead>
              <tr>
                <th>Interface</th>
                <th>IP Address</th>
                <th>Subnet Mask</th>
                <th>Gateway</th>
                <th>DNS Primary</th>
                <th>DNS Secondary</th>
                <th>Güncellendi</th>
                <th>İşlem</th>
              </tr>
            </thead>
            <tbody>
              {interfaces.map(inf => (
                <tr key={inf._id}>
                  <td>{inf.interface_name}</td>
                  <td>{inf.ip_address}</td>
                  <td>{inf.subnet_mask}</td>
                  <td>{inf.gateway || "-"}</td>
                  <td>{inf.dns_primary || "-"}</td>
                  <td>{inf.dns_secondary || "-"}</td>
                  <td>{inf.updated_at || "-"}</td>
                  <td>
                    <button
                      className="btn btn-sm btn-primary"
                      onClick={() => handleEditClick(inf)}
                    >
                      Düzenle
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Eğer edit moddaysak, "Arayüz Güncelle" formu */}
      {isEditMode ? (
        <div className="card">
          <div className="card-header">Arayüz Güncelle</div>
          <div className="card-body">
            <form onSubmit={handleUpdate}>
              <div className="mb-3">
                <label>Interface Adı (değiştirilemez)</label>
                <input
                  className="form-control"
                  value={editInterfaceName}
                  onChange={(e) => setEditInterfaceName(e.target.value)}
                  disabled
                />
              </div>
              <div className="mb-3">
                <label>IP Address</label>
                <input
                  className="form-control"
                  value={editIp}
                  onChange={(e) => setEditIp(e.target.value)}
                />
              </div>
              <div className="mb-3">
                <label>Subnet Mask</label>
                <input
                  className="form-control"
                  value={editMask}
                  onChange={(e) => setEditMask(e.target.value)}
                />
              </div>
              <div className="mb-3">
                <label>Gateway (opsiyonel)</label>
                <input
                  className="form-control"
                  value={editGateway}
                  onChange={(e) => setEditGateway(e.target.value)}
                />
              </div>
              <div className="mb-3">
                <label>DNS Primary (opsiyonel)</label>
                <input
                  className="form-control"
                  value={editDnsPrimary}
                  onChange={(e) => setEditDnsPrimary(e.target.value)}
                />
              </div>
              <div className="mb-3">
                <label>DNS Secondary (opsiyonel)</label>
                <input
                  className="form-control"
                  value={editDnsSecondary}
                  onChange={(e) => setEditDnsSecondary(e.target.value)}
                />
              </div>
              <button className="btn btn-success" type="submit">Kaydet</button>
              <button
                className="btn btn-secondary ms-2"
                type="button"
                onClick={handleCancelEdit}
              >
                Vazgeç
              </button>
            </form>
          </div>
        </div>
      ) : (
        // Edit mod değilse "Yeni Ekle" formu
        <div className="card">
          <div className="card-header">Yeni/Değiştir IP Ayarı</div>
          <div className="card-body">
            <form onSubmit={handleSave}>
              <div className="mb-3">
                <label className="form-label">Interface Adı</label>
                <input
                  type="text"
                  className="form-control"
                  value={intfName}
                  onChange={(e) => setIntfName(e.target.value)}
                />
              </div>
              <div className="mb-3">
                <label className="form-label">IP Address</label>
                <input
                  type="text"
                  className="form-control"
                  value={ipAddress}
                  onChange={(e) => setIpAddress(e.target.value)}
                  placeholder="192.168.1.50"
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Subnet Mask</label>
                <input
                  type="text"
                  className="form-control"
                  value={subnetMask}
                  onChange={(e) => setSubnetMask(e.target.value)}
                  placeholder="255.255.255.0"
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Gateway (opsiyonel)</label>
                <input
                  type="text"
                  className="form-control"
                  value={gateway}
                  onChange={(e) => setGateway(e.target.value)}
                  placeholder="192.168.1.1"
                />
              </div>
              <div className="mb-3">
                <label className="form-label">DNS Primary (opsiyonel)</label>
                <input
                  type="text"
                  className="form-control"
                  value={dnsPrimary}
                  onChange={(e) => setDnsPrimary(e.target.value)}
                  placeholder="8.8.8.8"
                />
              </div>
              <div className="mb-3">
                <label className="form-label">DNS Secondary (opsiyonel)</label>
                <input
                  type="text"
                  className="form-control"
                  value={dnsSecondary}
                  onChange={(e) => setDnsSecondary(e.target.value)}
                  placeholder="8.8.4.4"
                />
              </div>
              <button className="btn btn-primary" type="submit">
                Kaydet
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default NetworkInterfacesPage;
