// File: src/pages/FirewallRulesPage.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
// DnD
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";

function FirewallRulesPage() {
  const [rules, setRules] = useState([]);

  // Form
  const [ruleId, setRuleId] = useState(null);
  const [ruleName, setRuleName] = useState("");
  const [sourceIps, setSourceIps] = useState("");
  const [port, setPort] = useState("");
  const [protocol, setProtocol] = useState("TCP");
  const [action, setAction] = useState("ALLOW");
  const [direction, setDirection] = useState("IN");
  const [profile, setProfile] = useState("Any");
  const [description, setDescription] = useState("");
  const [enabled, setEnabled] = useState(true);
  const [priority, setPriority] = useState(100);
  const [scheduleStart, setScheduleStart] = useState("");
  const [scheduleEnd, setScheduleEnd] = useState("");
  const [daysOfWeek, setDaysOfWeek] = useState([]);
  const [groupId, setGroupId] = useState(""); // Hangi grupla ilişkilendirmek istersen

  const isEditMode = ruleId !== null;

  useEffect(() => {
    fetchRules();
  }, []);

  const fetchRules = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://127.0.0.1:8000/firewall/rules", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setRules(res.data);
    } catch (err) {
      alert("Kural listesi çekilemedi: " + err.message);
    }
  };

  const clearForm = () => {
    setRuleId(null);
    setRuleName("");
    setSourceIps("");
    setPort("");
    setProtocol("TCP");
    setAction("ALLOW");
    setDirection("IN");
    setProfile("Any");
    setDescription("");
    setEnabled(true);
    setPriority(100);
    setScheduleStart("");
    setScheduleEnd("");
    setDaysOfWeek([]);
    setGroupId("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    const ipsArray = sourceIps
      .split(",")
      .map((ip) => ip.trim())
      .filter((ip) => ip);

    const body = {
      rule_name: ruleName,
      source_ips: ipsArray,
      port: port ? Number(port) : null,
      protocol,
      action,
      direction,
      profile,
      description: description || null,
      enabled,
      priority: Number(priority),
      schedule_start: scheduleStart || null,
      schedule_end: scheduleEnd || null,
      days_of_week: daysOfWeek.map((d) => parseInt(d, 10)),
      group_id: groupId || null
    };

    try {
      let res;
      if (!isEditMode) {
        // create
        res = await axios.post("http://127.0.0.1:8000/firewall/rules", body, {
          headers: { Authorization: `Bearer ${token}` },
        });
        alert(res.data.message);
      } else {
        // update
        res = await axios.put(
          `http://127.0.0.1:8000/firewall/rules/${ruleId}`,
          body,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        alert(res.data.message);
      }
      clearForm();
      fetchRules();
    } catch (err) {
      alert("Hata: " + (err.response?.data?.detail || err.message));
    }
  };

  const handleEditClick = (rule) => {
    setRuleId(rule._id);
    setRuleName(rule.rule_name || "");
    setSourceIps(rule.source_ips?.join(",") || "");
    setPort(rule.port || "");
    setProtocol(rule.protocol || "TCP");
    setAction(rule.action || "ALLOW");
    setDirection(rule.direction || "IN");
    setProfile(rule.profile || "Any");
    setDescription(rule.description || "");
    setEnabled(rule.enabled !== false);
    setPriority(rule.priority || 100);
    setScheduleStart(rule.schedule_start || "");
    setScheduleEnd(rule.schedule_end || "");
    setDaysOfWeek(rule.days_of_week || []);
    setGroupId(rule.group_id || "");
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Bu kuralı silmek istediğinize emin misiniz?")) return;
    try {
      const token = localStorage.getItem("token");
      const res = await axios.delete(`http://127.0.0.1:8000/firewall/rules/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert(res.data.message);
      fetchRules();
    } catch (err) {
      alert("Silme hatası: " + (err.response?.data?.detail || err.message));
    }
  };

  const handleDayCheck = (dayIndex) => {
    if (daysOfWeek.includes(dayIndex)) {
      setDaysOfWeek(daysOfWeek.filter((d) => d !== dayIndex));
    } else {
      setDaysOfWeek([...daysOfWeek, dayIndex]);
    }
  };

  // Drag & Drop
  const onDragEnd = (result) => {
    if (!result.destination) return;
    const { source, destination } = result;

    const newRules = Array.from(rules);
    const [movedItem] = newRules.splice(source.index, 1);
    newRules.splice(destination.index, 0, movedItem);
    setRules(newRules);

    // opsiyonel: sunucuya priority update
    // buraya isterseniz PUT istekleri atabilirsiniz
  };

  return (
    <div>
      <h2 className="dashboard-title">Firewall Kuralları</h2>

      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="firewall-rules-list">
          {(provided) => (
            <div
              className="card mb-4"
              ref={provided.innerRef}
              {...provided.droppableProps}
            >
              <div className="card-header">Mevcut Kurallar</div>
              <div className="card-body p-0">
                <table className="table mb-0">
                  <thead>
                    <tr>
                      <th>#</th>
                      <th>Kural Adı</th>
                      <th>Port</th>
                      <th>Action</th>
                      <th>Direction</th>
                      <th>Group</th>
                      <th>Enabled?</th>
                      <th>İşlem</th>
                    </tr>
                  </thead>
                  <tbody>
                    {rules.map((r, index) => (
                      <Draggable key={r._id} draggableId={r._id} index={index}>
                        {(providedRow) => (
                          <tr
                            ref={providedRow.innerRef}
                            {...providedRow.draggableProps}
                            {...providedRow.dragHandleProps}
                          >
                            <td>{index + 1}</td>
                            <td>{r.rule_name}</td>
                            <td>{r.port || "-"}</td>
                            <td>{r.action}</td>
                            <td>{r.direction}</td>
                            <td>{r.group_id || "-"}</td>
                            <td>{r.enabled ? "Evet" : "Hayır"}</td>
                            <td>
                              <button
                                className="btn btn-sm btn-primary me-2"
                                onClick={() => handleEditClick(r)}
                              >
                                Düzenle
                              </button>
                              <button
                                className="btn btn-sm btn-danger"
                                onClick={() => handleDelete(r._id)}
                              >
                                Sil
                              </button>
                            </td>
                          </tr>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </Droppable>
      </DragDropContext>

      {/* Kural formu */}
      <div className="card">
        <div className="card-header">
          {isEditMode ? "Kural Düzenle" : "Yeni Kural Ekle"}
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label>Kural Adı</label>
              <input
                className="form-control"
                required
                value={ruleName}
                onChange={(e) => setRuleName(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <label>Kaynak IP(ler) (virgülle ayır)</label>
              <input
                className="form-control"
                value={sourceIps}
                onChange={(e) => setSourceIps(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <label>Port</label>
              <input
                type="number"
                className="form-control"
                value={port}
                onChange={(e) => setPort(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <label>Protocol</label>
              <select
                className="form-select"
                value={protocol}
                onChange={(e) => setProtocol(e.target.value)}
              >
                <option value="TCP">TCP</option>
                <option value="UDP">UDP</option>
              </select>
            </div>
            <div className="mb-3">
              <label>Action</label>
              <select
                className="form-select"
                value={action}
                onChange={(e) => setAction(e.target.value)}
              >
                <option value="ALLOW">ALLOW</option>
                <option value="DENY">DENY</option>
              </select>
            </div>
            <div className="mb-3">
              <label>Direction</label>
              <select
                className="form-select"
                value={direction}
                onChange={(e) => setDirection(e.target.value)}
              >
                <option value="IN">IN</option>
                <option value="OUT">OUT</option>
              </select>
            </div>
            <div className="mb-3">
              <label>Profile</label>
              <select
                className="form-select"
                value={profile}
                onChange={(e) => setProfile(e.target.value)}
              >
                <option value="Any">Any</option>
                <option value="Domain">Domain</option>
                <option value="Private">Private</option>
                <option value="Public">Public</option>
              </select>
            </div>
            <div className="mb-3">
              <label>Açıklama</label>
              <input
                className="form-control"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              />
            </div>
            <div className="mb-3 form-check">
              <input
                type="checkbox"
                className="form-check-input"
                checked={enabled}
                onChange={(e) => setEnabled(e.target.checked)}
                id="chkEnabled"
              />
              <label className="form-check-label" htmlFor="chkEnabled">
                Kural Etkin
              </label>
            </div>
            <div className="mb-3">
              <label>Öncelik</label>
              <input
                type="number"
                className="form-control"
                value={priority}
                onChange={(e) => setPriority(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <label>Zaman Başlangıcı (HH:MM)</label>
              <input
                type="time"
                className="form-control"
                value={scheduleStart}
                onChange={(e) => setScheduleStart(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <label>Zaman Sonu (HH:MM)</label>
              <input
                type="time"
                className="form-control"
                value={scheduleEnd}
                onChange={(e) => setScheduleEnd(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <label>Haftanın Günleri</label>
              <div>
                {["Pzt","Sal","Çar","Per","Cum","Cmt","Paz"].map((label, idx) => (
                  <div key={idx} className="form-check form-check-inline">
                    <input
                      type="checkbox"
                      className="form-check-input"
                      checked={daysOfWeek.includes(idx)}
                      onChange={() => handleDayCheck(idx)}
                      id={`chkDay${idx}`}
                    />
                    <label htmlFor={`chkDay${idx}`} className="form-check-label">
                      {label}
                    </label>
                  </div>
                ))}
              </div>
            </div>
            <div className="mb-3">
              <label>Grup (ID değeri, opsiyonel)</label>
              <input
                className="form-control"
                value={groupId}
                onChange={(e) => setGroupId(e.target.value)}
                placeholder="Grup ID girerseniz bu kurala grup atarsınız."
              />
            </div>
            <button type="submit" className="btn btn-primary">
              {isEditMode ? "Güncelle" : "Ekle"}
            </button>
            {isEditMode && (
              <button
                type="button"
                className="btn btn-secondary ms-2"
                onClick={clearForm}
              >
                Vazgeç
              </button>
            )}
          </form>
        </div>
      </div>
    </div>
  );
}

export default FirewallRulesPage;
