// File: src/pages/LoginPage.jsx
import React, { useState } from "react";
import axios from "axios";

function LoginPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      // 1) Backend'e POST isteği
      const response = await axios.post("http://127.0.0.1:8000/auth/login", {
        username,
        password
      });

      console.log("Login response data:", response.data);

      // 2) Dönen JWT token'ı localStorage'e yaz
      if (response.data.access_token) {
        localStorage.setItem("token", response.data.access_token);
        alert("Giriş başarılı! Token kaydedildi.");
        // 3) Anasayfaya yönlendirebilirsiniz
        window.location.href = "/";
      } else {
        // Token yoksa beklenmeyen bir durum
        alert("Sunucu JWT token döndürmedi, lütfen kontrol edin.");
      }
    } catch (error) {
      console.error("Login error:", error);
      // Hata mesajı
      if (error.response && error.response.data && error.response.data.detail) {
        alert("Giriş hatası: " + error.response.data.detail);
      } else {
        alert("Giriş hatası: " + error.message);
      }
    }
  };

  return (
    <div
      className="d-flex justify-content-center align-items-center vh-100"
      style={{ backgroundColor: "#f3f4f6" }}
    >
      <div className="card shadow" style={{ width: "420px" }}>
        <div className="card-header text-center bg-primary text-white">
          <h4 className="mb-0">NetGate Firewall Login</h4>
        </div>

        <div className="card-body">
          <form onSubmit={handleLogin}>
            {/* Kullanıcı Adı */}
            <div className="mb-3">
              <label className="form-label">Kullanıcı Adı</label>
              <input
                type="text"
                className="form-control"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>

            {/* Parola */}
            <div className="mb-3">
              <label className="form-label">Parola</label>
              <input
                type="password"
                className="form-control"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>

            {/* Giriş Butonu */}
            <button type="submit" className="btn btn-primary w-100">
              Giriş
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
