import React, { useState, useEffect } from 'react';
import axios from 'axios';

function LogsPage() {
  const [logs, setLogs] = useState([]);
  const [level, setLevel] = useState("");
  const [ipFilter, setIpFilter] = useState("");

  const fetchLogs = async () => {
    try {
      const token = localStorage.getItem("token");
      const params = {};
      if (level) params.level = level;
      if (ipFilter) params.ip = ipFilter;

      const resp = await axios.get("/api/logs/", {
        headers: { Authorization: `Bearer ${token}` },
        params
      });
      setLogs(resp.data);
    } catch (error) {
      alert("Log listesi alınamadı.");
    }
  };

  useEffect(() => {
    fetchLogs();
  }, []);

  return (
    <div>
      <h2 className="mb-4">Log Yönetimi</h2>

      <div className="mb-3 d-flex">
        <select className="form-select w-auto me-2"
          value={level}
          onChange={(e) => setLevel(e.target.value)}>
          <option value="">Hepsi</option>
          <option value="ERROR">ERROR</option>
          <option value="WARN">WARN</option>
          <option value="INFO">INFO</option>
        </select>

        <input
          className="form-control w-auto me-2"
          placeholder="IP filtre"
          value={ipFilter}
          onChange={(e) => setIpFilter(e.target.value)}
        />

        <button className="btn btn-primary" onClick={fetchLogs}>
          Filtre Uygula
        </button>
      </div>

      <table className="table table-striped">
        <thead>
          <tr>
            <th>Seviye</th>
            <th>Tarih</th>
            <th>Mesaj</th>
            <th>Kaynak IP</th>
          </tr>
        </thead>
        <tbody>
          {logs.map((log) => (
            <tr key={log._id || log.id}>
              <td>{log.level}</td>
              <td>{log.timestamp}</td>
              <td>{log.message}</td>
              <td>{log.source_ip}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default LogsPage;
