import React, { useState } from "react";
import axios from "axios";

function LoginPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  // Giriş butonuna basılınca çağrılacak fonksiyon
  const handleLogin = async (e) => {
    e.preventDefault(); // Form submit davranışını önler
    try {
      // Burada arka ucunuzun /api/auth/login endpoint’ine POST isteği atıyoruz
      const response = await axios.post("/api/auth/login", {
        username,
        password,
      });

      // Dönen token'ı localStorage’a kaydederek oturum bilgisini tutabilirsiniz
      localStorage.setItem("token", response.data.access_token);

      alert("Giriş başarılı!");
      // Başarılı giriş sonrası isterseniz ana sayfaya (dashboard) yönlendirebilirsiniz:
      window.location.href = "/";
    } catch (error) {
      alert(
        "Giriş hatası: " + (error.response?.data?.detail || error.message)
      );
    }
  };

  return (
    <div
      className="d-flex justify-content-center align-items-center vh-100"
      style={{ backgroundColor: "#f3f4f6" }} // Hafif gri arka plan
    >
      <div className="card shadow" style={{ width: "420px" }}>
        {/* Kartın üst kısmı (Header) */}
        <div className="card-header text-center bg-primary text-white">
          <h4 className="mb-0">NetGate Firewall Login</h4>
        </div>

        {/* Kartın gövdesi */}
        <div className="card-body">
          <form onSubmit={handleLogin}>
            {/* Kullanıcı Adı */}
            <div className="mb-3">
              <label className="form-label">Kullanıcı Adı</label>
              <input
                type="text"
                className="form-control"
                placeholder="admin"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>

            {/* Parola */}
            <div className="mb-3">
              <label className="form-label">Parola</label>
              <input
                type="password"
                className="form-control"
                placeholder="••••••••"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>

            {/* Giriş Butonu */}
            <button type="submit" className="btn btn-primary w-100">
              Giriş
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
